/* eslint-disable @eslint-react/no-children-map */
import type { ReactElement } from "react";
import { Children, useEffect, useRef, useState } from "react";

import { classNames } from "../classNames";
import { useListGesture } from "../hooks/useListGesture";
import type { HTMLDivProps } from "../htmlTypes";
import { renderOrClone } from "../renderOrClone";
import type { AccordionHeaderPropsType } from "./AccordionHeader";
import type { AccordionItemProps } from "./AccordionItem";

import style from "./Accordion.module.css";

export type AccordionContainerProps = HTMLDivProps & {
  children: Array<ReactElement> | ReactElement;
  headerLevel?: AccordionHeaderPropsType["level"];
  behavior?: "linked" | "isolated";
  openedDefault?: number;
  /**
   * You can control if the content rendered when opened={false}
   */
  renderContent?: boolean;
  variant?: "default" | "quiet";
};

function getAllItems(ref: HTMLElement): NodeList {
  return ref.querySelectorAll(`.${style.header}`);
}
function getSelectedItems(ref: HTMLElement): NodeList {
  return ref.querySelectorAll(`.${style.header}`);
}

export function AccordionContainer({
  behavior = "linked",
  headerLevel,
  children,
  openedDefault,
  renderContent,
  variant,
  ...props
}: AccordionContainerProps) {
  const [openedPanelIndex, setOpenedPanelIndex] = useState<number | undefined>(
    openedDefault,
  );
  const ref = useRef<HTMLDivElement>(null);
  const gesture = useListGesture({ getAllItems, getSelectedItems });
  useEffect(() => {
    if (ref.current) {
      gesture.setElement(ref.current);
      gesture.isActivated = () => {
        return !!document.activeElement?.classList.contains(style.header);
      };
    }

    return () => {
      gesture.setElement();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const onToggleOpened = (index: number) => {
    if (index === openedPanelIndex) {
      setOpenedPanelIndex(undefined);
    } else {
      setOpenedPanelIndex(index);
    }
  };

  return (
    <div
      className={classNames("flex_noreset", "w_full", "flex_col")}
      {...props}
      ref={ref}
    >
      {Children.map(children, (child, index) => {
        //controlled
        const childProps: Partial<AccordionItemProps> = {
          key: index,
          onToggleOpened: () => {
            onToggleOpened(index);
          },
          opened: index === openedPanelIndex,
          renderContent,
          variant,
          headerLevel,
        };
        if (behavior === "isolated") {
          //uncontrolled
          delete childProps.opened;
          if (openedDefault !== undefined) {
            childProps.openedDefault = index === openedDefault;
          }
        }
        return renderOrClone(child, childProps);
      })}
    </div>
  );
}

AccordionContainer.displayName = "Accordion.Container";

import { type ReactNode, type Ref, forwardRef } from "react";

import { classNames } from "../classNames";
import type { HTMLButtonProps } from "../htmlTypes";

import styles from "./Accordion.module.css";

type HeaderLevel = 2 | 3 | 4 | 5 | 6;

export type AccordionHeaderPropsType = {
  level?: HeaderLevel;
  opened: boolean;
  disabled?: boolean; // state enabled/disabled
  controlId: string;
  sectionId: string;
  icon?: ReactNode;
  header?: string;
  description?: string;
  width: "full" | "compact";
  onClick: () => unknown;
  chevronPosition?: "leading" | "trailing";
  affix?: ReactNode;
} & Omit<HTMLButtonProps, "type" | "onClick" | "disabled">;

function ChevronIcon({ opened }: { opened: boolean }) {
  return (
    <svg
      width="16"
      height="16"
      viewBox="0 0 16 16"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        fillRule="evenodd"
        clipRule="evenodd"
        d={
          opened
            ? "M7.99645 5.41421L13.1429 10.5607L13.85 9.85355L7.99645 4L2.1429 9.85355L2.85001 10.5607L7.99645 5.41421Z"
            : "M7.99641 10.5858L13.1429 5.43933L13.85 6.14644L7.99641 12L2.14285 6.14644L2.84996 5.43933L7.99641 10.5858Z"
        }
        fill="currentColor"
      />
    </svg>
  );
}

function HeaderLabel({
  children,
  className,
  level,
}: {
  children: ReactNode;
  className?: string;
  level?: HeaderLevel;
}) {
  switch (level) {
    case 2:
      return <h2 className={className}>{children}</h2>;
    case 3:
      return <h3 className={className}>{children}</h3>;
    case 4:
      return <h4 className={className}>{children}</h4>;
    case 5:
      return <h5 className={className}>{children}</h5>;
    case 6:
      return <h6 className={className}>{children}</h6>;
    default:
      return <span className={className}>{children}</span>;
  }
}

const headerDisabled = classNames(
  "cursor_not_allowed",
  "bg_disabled",
  "text_disabled",
);
const headerEnabled = classNames(
  "cursor_pointer",
  "bg_interactive",
  "text_default",
  "outline_focus_visible_inner",
);

export const AccordionHeader = forwardRef<
  HTMLButtonElement,
  AccordionHeaderPropsType
>(AccordionHeaderBase);

function AccordionHeaderBase(
  {
    level,
    controlId,
    sectionId,
    opened,
    onClick,
    icon,
    header,
    description,
    value,
    disabled = false,
    width,
    chevronPosition = "trailing",
    affix,
    ...props
  }: AccordionHeaderPropsType,
  ref?: Ref<HTMLButtonElement>,
) {
  return (
    <button
      id={controlId}
      aria-controls={sectionId}
      aria-expanded={opened}
      onClick={onClick}
      className={classNames(
        "flex_noreset",
        "flex_row",
        "flex_nowrap",
        "items_center",
        "gap_l",
        "bg_transparent",
        "py_density_m",
        "px_xl",
        "text_default",
        styles.header,
        {
          [headerEnabled]: !disabled,
          [headerDisabled]: disabled,
          w_fit: width === "compact",
        },
      )}
      disabled={disabled}
      ref={ref}
      data-test="panel.header"
      data-testid="panel.header"
      type="button"
      {...props}
      style={{ maxInlineSize: "100%" }}
    >
      {chevronPosition === "leading" && (
        <div
          className={classNames(
            "icon_size_xl",
            "text_default",
            "flex_noreset",
            "flex_row",
            "flex_nowrap",
            "items_center",
            "justify_center",
          )}
        >
          <ChevronIcon opened={opened} />
        </div>
      )}

      {icon ? (
        <span
          className={classNames(
            "pt_s",
            "text_default",
            "self_start",
            styles.icon,
          )}
        >
          {icon}
        </span>
      ) : null}
      <div
        className={classNames(
          "flex_noreset",
          "grow_1",
          "flex_col",
          "items_start",
          "gap_s",
          "py_s",
          "pr_m",
          "overflow_hidden",
        )}
      >
        <HeaderLabel
          level={level}
          className={classNames(
            "font_label_s_emphasized",
            "p_0",
            "m_0",
            "text_start",
          )}
        >
          {header}
        </HeaderLabel>
        {description ? (
          <span
            className={classNames("font_label_xs", "text_weak", "text_start")}
          >
            {description}
          </span>
        ) : null}
      </div>
      <div
        className={classNames(
          "flex_noreset",
          "flex_row",
          "items_center",
          "justify_end",
          "gap_m",
        )}
      >
        {value ? (
          <span className={classNames("font_label_xs", "text_weak")}>
            {value}
          </span>
        ) : null}

        {affix ? (
          <div
            className={classNames("flex_noreset", "flex_row", "items_center")}
          >
            {affix}
          </div>
        ) : null}
        {chevronPosition === "trailing" && (
          <div
            className={classNames(
              "icon_size_xl",
              "text_default",
              "flex_noreset",
              "flex_row",
              "flex_nowrap",
              "items_center",
              "justify_center",
            )}
          >
            <ChevronIcon opened={opened} />
          </div>
        )}
      </div>
    </button>
  );
}

AccordionHeaderBase.displayName = "Accordion.Header";

import { type ReactNode, type Ref, forwardRef } from "react";

import { useControl, useId } from "@qlik/sprout-css-hooks";

import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";
import {
  AccordionHeader,
  type AccordionHeaderPropsType,
} from "./AccordionHeader";

import style from "./Accordion.module.css";

type AccordionItemCommonPropsType = {
  variant?: "default" | "quiet";
  children: ReactNode;
  /**
   * You can control which tag is used. h2 with headerLevel={2}. span otherwise
   */
  headerLevel?: AccordionHeaderPropsType["level"];
  /**
   * You can control if the content rendered when opened={false}
   */
  renderContent?: boolean;
  opened?: boolean;
  openedDefault?: boolean;
  /**
   * When used alone you can use this property to make the accordion item width fit the content
   * It should never been used in an Accordion container
   * @default "full"
   */
  width?: "full" | "compact";
  disabled?: boolean;
  onToggleOpened?: () => void;
} & HTMLDivProps;

export type AccordionItemProps = AccordionItemCommonPropsType &
  Pick<
    AccordionHeaderPropsType,
    "header" | "description" | "value" | "icon" | "chevronPosition" | "affix"
  >;

export const AccordionItem = forwardRef<HTMLButtonElement, AccordionItemProps>(
  AccordionItemBase,
);

function AccordionItemBase(
  {
    variant = "default",
    id,
    children,
    headerLevel,
    opened,
    openedDefault,
    renderContent,
    onToggleOpened,
    icon,
    header,
    description,
    value,
    chevronPosition,
    affix,
    width = "full",
    disabled = false,
    ...rest
  }: AccordionItemProps,
  ref?: Ref<HTMLButtonElement>,
) {
  const controlled = useControl(
    {
      opened,
      openedDefault,
      onToggleOpened,
    },
    {
      onChangeKey: "onToggleOpened",
      valueKey: "opened",
      defaultValueKey: "openedDefault",
      selector: (e) => e,
    },
  );
  const safeComponentId = useId();
  const componentId = id || safeComponentId;
  const controlId = `AccordionItem__control--${componentId}`;
  const sectionId = `AccordionItem__content--${componentId}`;
  let shouldRender = true;

  if (!renderContent && !controlled.value) {
    shouldRender = false;
  }

  return (
    <div
      className={classNames(
        "flex_noreset",
        "flex_col",
        "overflow_hidden",
        "gap_m",
        {
          [style.divider]: variant === "default",
        },
      )}
      {...rest}
    >
      <AccordionHeader
        level={headerLevel}
        controlId={controlId}
        sectionId={sectionId}
        opened={!!controlled.value}
        onClick={() => {
          controlled.onChange(!controlled.value);
        }}
        icon={icon}
        header={header}
        description={description}
        value={value}
        chevronPosition={chevronPosition}
        affix={affix}
        width={width}
        disabled={disabled}
        data-variant={variant}
        ref={ref}
      />
      {shouldRender && (
        <div
          id={sectionId}
          role="region"
          data-expanded={controlled.value ? "true" : "false"}
          aria-labelledby={controlId}
          className={classNames("pt_0", "pr_xl", "pb_xl", "pl_xl", {
            hidden: !controlled.value,
          })}
          data-test="panel.section"
          data-testid="panel.section"
        >
          {children}
        </div>
      )}
    </div>
  );
}

AccordionItemBase.displayName = "Accordion.Item";

import { type ReactNode, type Ref, forwardRef } from "react";

import { tokens } from "@qlik/design-tokens";
import CrossOutline from "@qlik/sprout-icons/react/CrossOutline";

import { IconButton } from "../Button";
import { SEVERITY_ICONS } from "../Icons/SeverityIcon";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { HTMLDivProps } from "../htmlTypes";

import styles from "./AlertBanner.module.css";

type AlertBannerOnlyProps = {
  action?: ReactNode;
  content: ReactNode;
  dismissLabel?: string;
  justify?: "center";
  link?: ReactNode;
  onClickDismiss?: () => void;
  variant?: "error" | "warning" | "success" | "info";
};

export type AlertBannerProps = Omit<HTMLDivProps, "content" | "title"> &
  AlertBannerOnlyProps;

export const AlertBanner = forwardRef<HTMLDivElement, AlertBannerProps>(
  AlertBannerBase,
);

function AlertBannerBase(
  {
    variant,
    justify,
    content,
    children,
    action,
    link,
    dismissLabel,
    onClickDismiss,
    ...props
  }: AlertBannerProps,
  ref?: Ref<HTMLDivElement>,
) {
  const t = useI18n();
  const dismissLabelDefault = t("alertbanner.dismiss");

  const sharedContentClassnames = classNames(
    "m_0",
    "pr_m",
    "my_m",
    "font_body_s",
    "text_default",
    "overflow_hidden",
    {
      grow: justify !== "center",
      overflow_y_auto: typeof content !== "string",
      overflow_x_hidden: typeof content !== "string",
      [styles.content_max_height]: typeof content !== "string",
      [styles.content_max_rows]: typeof content === "string",
    },
  );

  return (
    <div
      role="status"
      aria-live="polite"
      className={classNames("bg_default", "w_full")}
      data-variant={variant || "info"}
      ref={ref}
      {...props}
    >
      <div
        className={classNames(
          "flex_noreset",
          "items_center",
          "border_box",
          "border_b_default",
          {
            bg_danger_weak: variant === "error",
            border_danger: variant === "error",
            bg_warning_weak: variant === "warning",
            border_warning: variant === "warning",
            bg_success_weak: variant === "success",
            border_success: variant === "success",
            bg_info_weak: variant === "info" || !variant,
            border_info: variant === "info" || !variant,
          },
        )}
        style={{ minBlockSize: tokens.common_sizing_5xl }}
      >
        <div
          className={classNames(
            "flex_noreset",
            "border_box",
            "w_full",
            "items_center",
            "grow",
            "flex_row",
            "justify_start",
            {
              j_center: justify === "center",
            },
          )}
        >
          <span
            className={classNames(
              "flex_noreset",
              "shrink_0",
              "items_center",
              "py_l",
              "px_xl",
              "icon_size_xl",
            )}
          >
            {SEVERITY_ICONS[variant || "info"]}
          </span>
          <div className={sharedContentClassnames}>{content}</div>
          {children ? (
            <div className={sharedContentClassnames}>{children}</div>
          ) : null}
          {link ? (
            <div
              className={classNames(
                "flex",
                "items_center",
                "justify_self_end",
                {
                  pl_0: justify !== "center",
                  pl_m: justify === "center",
                  pr_m: !onClickDismiss || !!action,
                },
              )}
            >
              {link}
            </div>
          ) : null}
          {action ? (
            <div
              className={classNames(
                "flex",
                "items_center",
                "justify_self_end",
                "pl_0",
                {
                  pr_m: !onClickDismiss,
                },
              )}
            >
              {action}
            </div>
          ) : null}
        </div>

        {onClickDismiss ? (
          <span
            className={classNames("px_m", "mr_auto", {
              pl_0: !action && !link,
            })}
          >
            <IconButton
              variant="quiet"
              onClick={() => {
                onClickDismiss();
              }}
              label={dismissLabel || dismissLabelDefault}
              icon={<CrossOutline />}
            />
          </span>
        ) : null}
      </div>
    </div>
  );
}

AlertBannerBase.displayName = "AlertBanner";

import { useEffect, useRef, useState } from "react";

import CrossOutline from "@qlik/sprout-icons/react/CrossOutline";

import { IconButton } from "../Button";
import { SEVERITY_ICONS } from "../Icons/SeverityIcon";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { AlertInlineProps } from "./AlertInline.types";

export function AlertInline({
  layout = "vertical",
  severity,
  title,
  message,
  children,
  actionBar,
  onDismiss,
  labelDismiss,
  ...props
}: AlertInlineProps) {
  const content = message || children;
  const contentRef = useRef<HTMLDivElement>(null);
  const [isContentWrapping, setIsContentWrapping] = useState(false);

  const t = useI18n();
  const labelDismissDefault = t("alertinline.dismiss");

  // Check if content is wrapping
  // Note: it's not reacting on window resize
  useEffect(() => {
    const element = contentRef.current;

    if (element) {
      const style = window.getComputedStyle(element);
      const lineHeight = parseFloat(style.lineHeight);
      const height = element.offsetHeight;
      const isWrapping = height > lineHeight;
      setIsContentWrapping(isWrapping);
    }
  }, []);

  return (
    <div
      role="status"
      aria-live="polite"
      {...props}
      className={classNames(
        "w_full",
        "flex",
        "border_box",
        "flex_row",
        "items_start",
        "shrink_0",
        "p_m",
        "bg_default",
        "border_default",
        "radius_subtle",

        {
          border_info: !!severity || severity === "info",
          border_warning: severity === "warning",
          border_error: severity === "error",
          border_success: severity === "success",
        },
      )}
    >
      <div
        className={classNames(
          "p_m",
          "pr_l",
          "icon_size_xl",
          "flex",
          "justify_center",
          "items_center",
          {
            as_center: !isContentWrapping && !title,
          },
        )}
      >
        {SEVERITY_ICONS[severity || "info"]}
      </div>
      <div
        className={classNames(
          "py_m",
          "pr_m",
          "flex",
          "flex_1",
          "justify_between",
          "overflow_hidden",
          {
            col: layout === "vertical",
            row: layout === "horizontal",
            gap_xl: layout === "horizontal",
            gap_m: layout === "vertical",
          },
        )}
      >
        <div
          className={classNames("flex", "gap_s", {
            j_center: !isContentWrapping,
          })}
        >
          {title ? (
            <strong
              className={classNames(
                "font_label_s_emphasized",
                "text_default",
                "text_wrap",
                "break_words",
              )}
            >
              {title}
            </strong>
          ) : null}
          {content ? (
            <div
              className={classNames(
                "font_label_s",
                "text_default",
                "text_wrap",
                "break_words",
                "flex",
                {
                  mb_s: layout === "vertical" && !!actionBar,
                },
              )}
              ref={contentRef}
            >
              {content}
            </div>
          ) : null}
        </div>
        {actionBar ? (
          <div className={classNames("shrink_0")}>
            <div
              className={classNames(
                "flex",
                "gap_xl",
                "flex_row",
                "items_center",
              )}
            >
              {actionBar}
            </div>
          </div>
        ) : null}
      </div>
      {onDismiss ? (
        <div
          className={classNames({
            pt_m: layout === "horizontal" && !!actionBar,
          })}
        >
          <IconButton
            variant="quiet"
            onClick={() => {
              onDismiss();
            }}
            icon={<CrossOutline />}
            label={labelDismiss || labelDismissDefault}
          />
        </div>
      ) : null}
    </div>
  );
}

import { type ReactNode, useState } from "react";

import { Button } from "../Button";
import { SEVERITY_ICONS } from "../Icons/SeverityIcon";
import { Modal, type ModalCompositionProps } from "../Modal";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";

export type AlertModalProps = Omit<ModalCompositionProps["Root"], "variant"> & {
  variant?: "positive" | "destructive" | "error" | "warning" | "information";
  title: string;
  children?: ReactNode;
  details?: ReactNode;
  footerLeft?: ReactNode;
  footerRight: ReactNode;
};

export function AlertModal({
  title,
  children,
  footerLeft,
  footerRight,
  details,
  "data-testid": dataTestId,
  variant = "information",
  ...props
}: AlertModalProps) {
  const t = useI18n();
  const [displayDetails, setDisplayDetails] = useState(false);

  let icon = null;

  if (variant === "error") {
    icon = SEVERITY_ICONS.error;
  } else if (variant === "warning") {
    icon = SEVERITY_ICONS.warning;
  } else if (variant === "information") {
    icon = SEVERITY_ICONS.info;
  }

  return (
    <Modal.Root
      {...props}
      role="alertdialog"
      preventEscaping
      preventInteractiveBackdrop
      aria-label={title}
      data-testid={dataTestId}
    >
      <div className={classNames("flex_noreset", "flex_row")}>
        {!!icon && (
          <div className={classNames("pt_xxl", "pl_xxl", "icon_size_xxl")}>
