import type { CSSProperties, ReactElement, ReactNode } from "react";

import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { HTMLImageProps, HTMLSpanProps } from "../htmlTypes";

import style from "./Avatar.module.css";

export type AvatarProps = Omit<HTMLSpanProps, "size"> & {
  size?: "xs" | "s" | "m" | "l" | "xl" | "illustration";
  text?: string;
  badge?: ReactNode;
  icon?: ReactElement;
  img?: HTMLImageProps;
  bgIndex?: number;
};

const AVATAR_MODULO = 8;

export function Avatar({
  size = "m",
  text,
  icon,
  img,
  badge,
  bgIndex,
  ...props
}: AvatarProps) {
  let child = null;
  const t = useI18n();
  const a11yProps: Partial<HTMLSpanProps> = {};
  const customStyle: CSSProperties = {};

  if (img) {
    const { src, alt, ...rest } = img;
    child = <img alt={alt} src={src} {...rest} />;
  } else if (icon) {
    child = icon;
  } else if (text) {
    child = text.slice(0, 2);
    a11yProps.role = "img";

    if (!props["aria-label"]) {
      a11yProps["aria-label"] = t("avatar.label.alt");
    }
  }

  let bgClassName;
  if (bgIndex !== undefined) {
    const bgIndexMod = `bg${bgIndex % AVATAR_MODULO}` as keyof typeof style;
    bgClassName = style[bgIndexMod];
  }

  return (
    <span
      {...props}
      {...a11yProps}
      style={customStyle}
      className={classNames(
        "relative",
        "flex_noreset",
        "shrink_0",
        "justify_center",
        "items_center",
        "radius_round",
        "size_3xl",
        "font_label_m",
        style.avatar,
        {
          [`${bgClassName}`]: bgIndex !== undefined,
          [style.xs]: size === "xs",
          [style.s]: size === "s",
          [style.l]: size === "l",
          [style.xl]: size === "xl",
          [style.illustration]: size === "illustration",
        },
      )}
    >
      {child}
      {badge ? <span className={style.badge}>{badge}</span> : null}
    </span>
  );
}

import { type CSSProperties, type Ref, forwardRef } from "react";

import { type TypographyProps, getTypographyClassName } from "../Typography";
import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";
import { type BackgroundProps, getBackgroundProps } from "./background";
import { type BorderProps, getBorderProps } from "./border";
import { type BoxShadowProps, getBoxShadow } from "./boxShadow";
import { type FlexArgs, type FlexProps, getFlexProps } from "./flex";
import { type InlineStyleProps, getInlineStyleProps } from "./inline";
import { type OverflowProps, getOverflowProps } from "./overflow";
import { type PaddingProps, getPaddingProps } from "./padding";

export type BoxPropsOnly = InlineStyleProps &
  PaddingProps &
  FlexProps &
  BorderProps &
  BackgroundProps &
  BoxShadowProps &
  OverflowProps & {
    typography?: TypographyProps;
  };

export type BoxProps = BoxPropsOnly &
  HTMLDivProps & { className?: string; style?: CSSProperties };

/**
 * @deprecated Box is deprecated, you must use classNames instead like so:
 * <div className={classNames('flex', 'flex_row', 'border_box')}></div>
 */
export const Box = forwardRef<HTMLDivElement, BoxProps>(BoxBase);

function BoxBase({ children, ...props }: BoxProps, ref?: Ref<HTMLDivElement>) {
  return (
    <div {...getBoxProps(props)} ref={ref}>
      {children}
    </div>
  );
}

BoxBase.displayName = "Box";

/**
 * @deprecated getBoxProps is deprecated, you must use classNames instead
 * classNames('flex', 'row', 'border-box')
 */
export function getBoxProps(
  {
    typography,
    ...props
  }: BoxPropsOnly & { className?: string; style?: CSSProperties },
  opt: FlexArgs = { apply: true },
) {
  const { className, ...boxProps } = getOverflowProps(
    getBoxShadow(
      getBackgroundProps(
        getBorderProps(
          getFlexProps(getPaddingProps(getInlineStyleProps(props)), opt),
        ),
      ),
    ),
  );

  return {
    ...boxProps,
    className: classNames({
      [`${typography && getTypographyClassName(typography)}`]: !!typography,
      [className]: true,
    }),
  };
}

import { classNames } from "../classNames";
import type { BorderRadius } from "./tokens";

export type Border =
  | "none"
  | "default"
  | "weak"
  | "moderate"
  | "strong"
  | "extra-strong";

export type BorderProps = {
  borderRadius?: BorderRadius;
  border?: Border;
  borderTop?: Border;
  borderRight?: Border;
  borderBottom?: Border;
  borderLeft?: Border;
  borderWidth?: "0" | "default" | "strong";
};

const BORDER_PROPS: Array<keyof BorderProps> = [
  "border",
  "borderTop",
  "borderRight",
  "borderBottom",
  "borderLeft",
  "borderWidth",
  "borderRadius",
];

const STYLE_CONDITIONS: Record<
  keyof BorderProps,
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  (k: any) => Record<string, boolean>
> = {
  border: (border: Border) => ({
    border_none: border === "none",
    border_default: border === "default",
    border_weak: border === "weak",
    border_moderate: border === "moderate",
    border_strong: border === "strong",
    border_extra_strong: border === "extra-strong",
  }),
  borderTop: (borderTop: Border) => ({
    border_t_none: borderTop === "none",
    border_t_default: borderTop === "default",
    border_t_weak: borderTop === "weak",
    border_t_moderate: borderTop === "moderate",
    border_t_strong: borderTop === "strong",
    border_t_extra_strong: borderTop === "extra-strong",
  }),
  borderRight: (borderRight: Border) => ({
    border_r_none: borderRight === "none",
    border_r_default: borderRight === "default",
    border_r_weak: borderRight === "weak",
    border_r_moderate: borderRight === "moderate",
    border_r_strong: borderRight === "strong",
    border_r_extra_strong: borderRight === "extra-strong",
  }),
  borderBottom: (borderBottom: Border) => ({
    border_b_none: borderBottom === "none",
    border_b_default: borderBottom === "default",
    border_b_weak: borderBottom === "weak",
    border_b_moderate: borderBottom === "moderate",
    border_b_strong: borderBottom === "strong",
    border_b_extra_strong: borderBottom === "extra-strong",
  }),
  borderLeft: (borderLeft: Border) => ({
    border_l_none: borderLeft === "none",
    border_l_default: borderLeft === "default",
    border_l_weak: borderLeft === "weak",
    border_l_moderate: borderLeft === "moderate",
    border_l_strong: borderLeft === "strong",
    border_l_extra_strong: borderLeft === "extra-strong",
  }),
  borderWidth: (borderWidth: "0" | "default" | "strong") => ({
    border_width_0: borderWidth === "0",
    border_width_default: borderWidth === "default",
    border_width_strong: borderWidth === "strong",
  }),
  borderRadius: (borderRadius: BorderRadius) => ({
    radius_subtle: borderRadius === "subtle",
    radius_soft: borderRadius === "soft",
    radius_cushiony: borderRadius === "cushiony",
    radius_pill: borderRadius === "pill",
    radius_round: borderRadius === "round",
    radius_nested_subtle_xs: borderRadius === "nested-subtle-xs",
    radius_nested_soft_xs: borderRadius === "nested-soft-xs",
    radius_nested_soft_s: borderRadius === "nested-soft-s",
    radius_nested_cushiony_xs: borderRadius === "nested-cushiony-xs",
    radius_nested_cushiony_s: borderRadius === "nested-cushiony-s",
    radius_nested_cushiony_m: borderRadius === "nested-cushiony-m",
    radius_twice_soft_xs: borderRadius === "twice-soft-xs",
    radius_twice_cushiony_s_xs: borderRadius === "twice-cushiony-s-xs",
    radius_twice_cushiony_m_s: borderRadius === "twice-cushiony-m-s",
  }),
};

export function getBorderProps<T extends BorderProps & { className?: string }>(
  props: T,
) {
  const { className, ...rest } = props;
  const cond = BORDER_PROPS.reduce<Record<string, boolean>>(
    (acc, key) => {
      if (key in props) {
        Object.assign(acc, STYLE_CONDITIONS[key](props[key]));
        delete rest[key];
      }
      return acc;
    },
    {
      [className ?? ""]: !!className,
    },
  );
  const newClassName = classNames(cond);
  return { className: newClassName, ...rest };
}

import { classNames } from "../classNames";
import type { Align, AlignContent, Density, Justify, Spacing } from "./tokens";

export type FlexProps = {
  boxSizing?: "border-box" | "content-box";
  wrap?: "wrap" | "nowrap" | "wrap-reverse";

  direction?: "row" | "row-reverse" | "col" | "col-reverse";

  alignContent?: AlignContent;
  alignItems?: AlignContent | "baseline";
  alignSelf?: Align;
  gap?: Spacing | Density;
  columnGap?: Spacing;
  rowGap?: Spacing;
  justify?: Justify;
  noShrink?: boolean;
};

export type FlexArgs = {
  apply: boolean;
};

const STYLE_CONDITIONS: Record<
  keyof FlexProps,
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  (k: any, opt?: FlexArgs) => Record<string, boolean>
> = {
  gap: (gap: Spacing | Density) => ({
    gap_0: gap === "0",
    gap_xs: gap === "xs",
    gap_s: gap === "s",
    gap_m: gap === "m",
    gap_l: gap === "l",
    gap_xl: gap === "xl",
    gap_xxl: gap === "xxl",
    gap_3xl: gap === "3xl",
    gap_density_xs: gap === "density-xs",
    gap_density_s: gap === "density-s",
    gap_density_m: gap === "density-m",
    gap_density_l: gap === "density-l",
    gap_density_xl: gap === "density-xl",
    gap_density_xxl: gap === "density-xxl",
    gap_density_3xl: gap === "density-3xl",
  }),
  rowGap: (rowGap: Spacing) => ({
    row_gap_0: rowGap === "0",
    row_gap_xs: rowGap === "xs",
    row_gap_s: rowGap === "s",
    row_gap_m: rowGap === "m",
    row_gap_l: rowGap === "l",
    row_gap_xl: rowGap === "xl",
    row_gap_xxl: rowGap === "xxl",
    row_gap_3xl: rowGap === "3xl",
  }),
  columnGap: (columnGap: Spacing) => ({
    col_gap_0: columnGap === "0",
    col_gap_xs: columnGap === "xs",
    col_gap_s: columnGap === "s",
    col_gap_m: columnGap === "m",
    col_gap_l: columnGap === "l",
    col_gap_xl: columnGap === "xl",
    col_gap_xxl: columnGap === "xxl",
    col_gap_3xl: columnGap === "3xl",
  }),
  boxSizing: (
    boxSizing: "border-box" | "content-box",
    opt: FlexArgs = { apply: true },
  ) => ({
    content_box: boxSizing === "content-box",
    border_box: boxSizing !== "content-box" && opt.apply,
  }),
  wrap: (wrap: "wrap" | "nowrap" | "wrap-reverse") => ({
    wrap: wrap === "wrap",
    wrap_reverse: wrap === "wrap-reverse",
    no_wrap: wrap === "nowrap",
  }),
  direction: (
    direction: "row" | "row-reverse" | "col" | "col-reverse",
    opt: FlexArgs = { apply: true },
  ) => ({
    row: direction === "row" || (opt.apply && !direction),
    row_reverse: direction === "row-reverse",
    col: direction === "col",
    col_reverse: direction === "col-reverse",
  }),
  noShrink: (noShrink: boolean) => ({
    shrink_0: noShrink,
  }),
  alignContent: (alignContent: AlignContent) => ({
    ac_center: alignContent === "center",
    ac_stretch: alignContent === "stretch",
    ac_start: alignContent === "start",
    ac_end: alignContent === "end",
    ac_between: alignContent === "between",
    ac_around: alignContent === "around",
  }),
  alignItems: (alignItems: AlignContent | "baseline") => ({
    ai_center: alignItems === "center",
    ai_stretch: alignItems === "stretch",
    ai_start: alignItems === "start",
    ai_end: alignItems === "end",
    ai_baseline: alignItems === "baseline",
  }),
  alignSelf: (alignSelf: Align) => ({
    as_center: alignSelf === "center",
    as_stretch: alignSelf === "stretch",
    as_start: alignSelf === "start",
    as_end: alignSelf === "end",
  }),
  justify: (justify: Justify) => ({
    j_around: justify === "around",
    j_between: justify === "between",
    j_center: justify === "center",
    j_end: justify === "end",
    j_start: justify === "start",
    j_evenly: justify === "evenly",
  }),
};

const FLEX_PROPS: Array<keyof FlexProps> = [
  "boxSizing",
  "wrap",
  "direction",
  "alignContent",
  "alignItems",
  "alignSelf",
  "gap",
  "columnGap",
  "rowGap",
  "justify",
  "noShrink",
];

export function getFlexProps<T extends FlexProps & { className?: string }>(
  props: T,
  opt: FlexArgs = { apply: true },
) {
  const { className, ...rest } = props;

  const cond = FLEX_PROPS.reduce(
    (acc, key) => {
      if (key in props) {
        Object.assign(acc, STYLE_CONDITIONS[key](props[key], opt));
        delete rest[key];
      } else {
        if (key === "direction" && opt.apply) {
          Object.assign(acc, STYLE_CONDITIONS.direction(undefined, opt));
        }
        if (key === "boxSizing" && opt.apply) {
          Object.assign(acc, STYLE_CONDITIONS.boxSizing(undefined, opt));
        }
      }
      return acc;
    },
    {
      flex: opt.apply,
    },
  );
  // We are fixing a bug here that need to escape ts
  // <Box className={sprout.classNames('col')} />; => col is considered false
  // eslint-disable-next-line @typescript-eslint/ban-ts-comment
  // @ts-ignore
  const newClassName = classNames(className, cond);

  return { className: newClassName, ...rest };
}

