import DropdownArrow from "@qlik/sprout-icons/react/DropdownArrow";

import { Badge, type BadgeProps } from "../Badge";
import { ProgressCircular } from "../Progress";
import { classNames } from "../classNames";
import type { HTMLButtonProps } from "../htmlTypes";

import style from "./Button.module.css";

const LABEL_LENGTH_FOR_TITLE = 20;

export type ButtonProps = Omit<HTMLButtonProps, "label"> & {
  /**
   * Variant of the button. destructive is deprecated you should use the dedicated property instead.
   */
  variant?: "primary" | "secondary" | "quiet" | "default" | "destructive";
  /**
   * Apply to variant primary and secondary for now
   */
  destructive?: boolean;
  badge?: BadgeProps;
  asDropdown?: boolean;
  justified?: boolean;
  size?: "small" | "default";
  label?: string;
  loading?: boolean;
  icon?: ReactNode;
};

export const Button = forwardRef(BaseButton);

function BaseButton(
  {
    variant = "default",
    badge,
    asDropdown,
    justified,
    size = "default",
    destructive,
    disabled,
    loading,
    label,
    type = "button",
    icon,
    title,
    ...props
  }: ButtonProps,
  ref?: Ref<HTMLButtonElement>,
) {
  const safeVariant =
    variant === "destructive" ? "primary" : variant || "default";
  const safeDestructive = destructive || variant === "destructive";

  return (
    <button
      ref={ref}
      type={type}
      data-surface="button"
      className={classNames(style.button, {
        [style.nolabel]: !label,
        [style.borderless]:
          safeVariant === "quiet" || safeVariant === "primary",
      })}
      data-variant={safeVariant}
      data-destructive={safeDestructive}
      data-size={size}
      disabled={disabled || loading}
      data-loading={loading}
      data-justified={justified || false}
      title={
        title ||
        (label && label.length > LABEL_LENGTH_FOR_TITLE ? label : undefined)
      }
      {...props}
    >
      <span className={style.content}>
        {icon ? <span data-type="icon">{icon}</span> : null}
        {label ? <span data-label>{label}</span> : null}
        {badge ? (
          <span className={style.badge}>
            <Badge {...badge} />
          </span>
        ) : null}
        {asDropdown ? <DropdownArrow data-dropdown data-type="icon" /> : null}
        {loading ? (
          <ProgressCircular
            data-loader
            size={size === "small" ? "s" : "m"}
            variant={destructive ? "destructive" : "default"}
          />
        ) : null}
      </span>
    </button>
  );
}

BaseButton.displayName = "Button";

import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";

import styles from "./ButtonGroup.module.css";

export type ButtonGroupProps = HTMLDivProps & {
  orientation?: "horizontal" | "vertical";
  fullWidth?: boolean;
};

export function ButtonGroup({
  orientation,
  fullWidth,
  children,
  ...props
}: ButtonGroupProps) {
  return (
    <div
      {...props}
      className={classNames(
        "flex",
        "flex_row",
        "border_box",
        styles.button_group,
        {
          col: orientation === "vertical",
          w_full: !!fullWidth,
          w_fit: !fullWidth,
        },
      )}
      role="group"
      data-variant={orientation || "horizontal"}
    >
      {children}
    </div>
  );
}

import { classNames } from "../classNames";
import type { HTMLSpanProps } from "../htmlTypes";

export type CharacterCountProps = Omit<HTMLSpanProps, "span"> & {
  currentCount: number;
  maxCount?: number;
};

export function CharacterCount({
  currentCount,
  maxCount = 100,
  ...props
}: CharacterCountProps) {
  const limitExceded = classNames("text_danger");
  return (
    <div
      className={classNames("text_weak", "font_body_s", "font_label_xs", {
        [limitExceded]: currentCount > maxCount || false,
      })}
    >
      <span {...props}>
        {currentCount} / {maxCount}
      </span>
    </div>
  );
}
CharacterCount.displayName = "CharacterCount";

import {
  type ReactNode,
  type Ref,
  forwardRef,
  useEffect,
  useImperativeHandle,
  useRef,
} from "react";

import { useId } from "@qlik/sprout-css-hooks";

import { HelperText } from "../HelperText";
import { InfoTooltipPrimitive } from "../_InfoTooltipPrimitive";
import { classNames } from "../classNames";
import { useValueControl } from "../hooks/useValueControl";
import type { HTMLInputProps } from "../htmlTypes";
import { CheckboxIndeterminate, CheckboxTick } from "./CheckboxTick";

import styles from "./Checkbox.module.css";

type CheckboxNativeProps = Omit<
  HTMLInputProps,
  "value" | "type" | "prefix" | "ref"
>;

export type CheckboxProps = CheckboxNativeProps & {
  indeterminate?: boolean;
  label?: string;
  helpText?: string;
  infoIconTooltip?: ReactNode;
};

export const Checkbox = forwardRef<HTMLInputElement, CheckboxProps>(
  CheckboxBase,
);

function CheckboxBase(
  props: CheckboxProps,
  ref?: Ref<HTMLInputElement | null>,
) {
  const checkboxRef = useRef<HTMLInputElement | null>(null);
  // Forward ref to parent ref
  useImperativeHandle(ref, () => checkboxRef.current as HTMLInputElement);

  const {
    id,
    label,
    helpText,
    disabled = false,
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    checked,
    indeterminate,
    infoIconTooltip,
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    onChange,
    ...rest
  } = props;

  const safeCheckboxId = useId(id);
  const labelId = `${safeCheckboxId}-label`;
  const helpTextId = `${safeCheckboxId}-helptext`;

  const controlled = useValueControl<CheckboxNativeProps>(props, {
    onChangeKey: "onChange",
    valueKey: "checked",
    defaultValueKey: "defaultChecked",
    selector: (e) => e.target.checked,
  });

  useEffect(() => {
    // indeterminate is a controlled value only
    if (checkboxRef.current) {
      checkboxRef.current.indeterminate = !!indeterminate;
    }
  }, [checkboxRef, indeterminate, controlled.checked]);
  const ariaProps: {
    "aria-checked"?: "true" | "false" | "mixed";
  } = {};
  if (indeterminate) {
    ariaProps["aria-checked"] = "mixed";
  } else if (typeof controlled.checked !== "undefined") {
    ariaProps["aria-checked"] = controlled.checked ? "true" : "false";
  }
  return (
    <span
      className={classNames(
        "inline_flex",
        "justify_start",
        "gap_m",
        "items_start",
        "relative",
        "w_full",
        styles.checkbox,
      )}
      data-disabled={disabled}
    >
      <input
        type="checkbox"
        className={classNames(
          "m_0",
          "p_0",
          "w_full",
          "h_full",
          "absolute",
          "left_0",
          "top_0",
          "opacity_0",
          "outline_none",
          {
            cursor_pointer: !disabled,
          },
        )}
        disabled={disabled}
        ref={checkboxRef}
        id={safeCheckboxId}
        {...ariaProps}
        {...rest}
        {...controlled}
      />
      <span
        className={classNames(
          "inline_flex",
          "border_box",
          "shrink_0",
          "items_center",
          "justify_center",
          "size_xl",
          "radius_subtle",
          styles.box,
        )}
      >
        {indeterminate ? <CheckboxIndeterminate /> : null}
        {!indeterminate ? <CheckboxTick /> : null}
      </span>
      {(typeof label === "string" || typeof helpText === "string") && (
        <div className={classNames("flex", "flex_col", "gap_s")}>
          {typeof label === "string" && (
            <div className={classNames("flex_noreset", "gap_m", "items_start")}>
              <label
                id={labelId}
                htmlFor={safeCheckboxId}
                className={classNames("font_label_s", {
                  text_default: !disabled,
                  cursor_pointer: !disabled,
                  text_disabled: disabled,
                })}
              >
                {label}
              </label>
              {infoIconTooltip ? (
                <span
                  className={classNames(
                    "z_stacked",
                    "flex_noreset",
                    "items_center",
                    "text_default",
                    "p_xs",
                    {
                      text_disabled: disabled,
                    },
                  )}
                >
                  <InfoTooltipPrimitive
                    // referencing the label for accessibility
                    aria-labelledby={labelId}
                    title={infoIconTooltip}
                    disabled={disabled}
                  />
                </span>
              ) : null}
            </div>
          )}
          {typeof helpText === "string" && (
            <HelperText
              id={helpTextId}
              helpText={helpText}
              variant="quiet"
              disabled={disabled}
            />
          )}
        </div>
      )}
    </span>
  );
}

CheckboxBase.displayName = "Checkbox";

import type { ReactNode } from "react";

import { Field, type FieldProps } from "../Field";
import { classNames } from "../classNames";
import { useFieldAriaProps } from "../hooks/useFieldAriaProps";
import { Checkbox, type CheckboxProps } from "./Checkbox";

export type CheckboxGroupProps = Omit<FieldProps, "children"> & {
  options?: Array<Omit<CheckboxProps, "ref">>;
  children?: ReactNode;
  name?: string;
};

export function CheckboxGroup({
  // name doesn't make any sens in this context
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  name,
  options,
  children,
  ...rest
}: CheckboxGroupProps) {
  const { fieldProps, ariaProps } = useFieldAriaProps({
    label: rest.label,
    helpText: rest.helpText,
    errorMessages: rest.errorMessages,
  });

  return (
    <Field {...fieldProps} {...rest}>
      <div
        className={classNames(
          "flex",
          "flex_col",
          "border_box",
          "gap_density_l",
        )}
        role="group"
        {...ariaProps}
      >
        {(options || []).map((opt) => (
          <Checkbox key={opt.label || opt.name} {...opt} />
        ))}
        {children}
      </div>
    </Field>
  );
}

CheckboxGroup.displayName = "CheckboxGroup";

import type { CSSProperties, KeyboardEvent, Ref } from "react";
import { forwardRef } from "react";

import { useControl } from "@qlik/sprout-css-hooks";

import type { HTMLDivProps } from "../htmlTypes";
import type { DialogAction, DialogProps, DialogState } from "./DialogState";

type A11yDialogPropsType = {
  onKeyDown?: (event: KeyboardEvent<HTMLDivElement>) => void;
};

export type DialogPropsType = HTMLDivProps &
  DialogProps & {
    "aria-modal"?: boolean | "true" | "false";
    className?: string;
    style?: CSSProperties;
  } & A11yDialogPropsType;

export const Dialog = forwardRef<HTMLDivElement, DialogPropsType>(DialogBase);

function DialogBase(props: DialogPropsType, ref?: Ref<HTMLDivElement>) {
  const { visible, ...rest } = props;
  if (!visible) {
    return null;
  }

  return <div tabIndex={-1} ref={ref} role="dialog" {...rest} data-dialog />;
}

DialogBase.displayName = "Dialog";

export function useDialogState(opts: Partial<DialogState & DialogAction>) {
  const controlled = useControl<boolean>(opts, {
    defaultValueKey: "defaultVisible",
    onChangeKey: "onClose",
    selector: () => false,
    valueKey: "visible",
  });

  return {
    visible: !!controlled.value,
    onClose: () => {
      controlled.onChange(false);
    },
  };
}

