import { type ReactNode, useEffect, useRef, useState } from "react";

import { Divider } from "../Divider";
import { classNames } from "../classNames";
import { useHasOverflow } from "../hooks/useHasOverflow";
import type { HTMLDivProps } from "../htmlTypes";

import style from "../Modal/Modal.module.css";

export type DialogContentProps = {
  children: ReactNode;
  padding?: boolean;
  overflowX?: "hidden" | "visible" | "scroll" | "auto";
  overflowY?: "hidden" | "visible" | "scroll" | "auto";
  divider?: "visible" | "none" | "auto";
} & Omit<HTMLDivProps, "children">;

export function DialogContent({
  children,
  padding,
  overflowX = "hidden",
  overflowY = "auto",
  divider = "auto",
  ...props
}: DialogContentProps) {
  const ref = useRef<HTMLDivElement | null>(null);
  const overflow = useHasOverflow(ref);
  const [scrollableState, setScrollableState] = useState(overflow.overflowY);

  let hasDivider = divider === "visible";
  if (!hasDivider) {
    if (divider === "auto" && scrollableState && overflowY === "auto") {
      hasDivider = true;
    }
  }

  useEffect(() => {
    if (overflow.overflowY !== scrollableState && overflowY === "auto") {
      // eslint-disable-next-line react-hooks/set-state-in-effect
      setScrollableState(overflow.overflowY);
    }
  }, [overflow.overflowY, scrollableState, overflowY]);
  return (
    <>
      {hasDivider ? <Divider /> : null}
      <div
        className={classNames(
          "text_default",
          "font_body_s",
          "flex_noreset",
          "flex_col",
          "border_box",
          "py_0",
          "grow_1",
          style.modal_body,
          {
            px_xxl: padding === undefined || !!padding,
            overflow_y_auto: overflowY === "auto",
            overflow_x_auto: overflowX === "auto",
            overflow_y_scroll: overflowY === "scroll",
            overflow_x_scroll: overflowX === "scroll",
            overflow_y_hidden: overflowY === "hidden",
            overflow_x_hidden: overflowX === "hidden",
          },
        )}
        data-testid="modal.content"
        role={overflowY === "auto" ? "region" : undefined}
        data-scrollable={
          overflowY === "auto" && scrollableState ? "true" : "false"
        }
        {...props}
        ref={ref}
        // as we have added region role we have covered the accessibility requirement
        // https://developer.mozilla.org/en-US/docs/Web/Accessibility/ARIA/Roles/region_role#scrolling_content_areas_with_overflow_text
        tabIndex={overflowY === "auto" && scrollableState ? 0 : -1}
      >
        {children}
      </div>
      {hasDivider ? <Divider /> : null}
    </>
  );
}

import { type ReactNode, useId } from "react";

import { DirectionLeftIcon } from "@qlik/sprout-icons/react";
import CrossOutline from "@qlik/sprout-icons/react/CrossOutline";

import { IconButton } from "../Button";
import { useModalContext } from "../Modal/ModalContext";
import { InfoTooltipPrimitive } from "../_InfoTooltipPrimitive";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { HTMLDivProps } from "../htmlTypes";

export type DialogHeaderProps = HTMLDivProps & {
  children: ReactNode;
  description?: ReactNode;
  descriptionId?: string;
  icon?: ReactNode;
  infoIconTooltip?: ReactNode;
  labelBack?: string;
  labelClose?: string;
  labelId?: string;
  onClickBack?: () => void;
  onClickClose?: () => void;
  preventClose?: boolean;
};

export function DialogHeader({
  "data-testid": dataTestId = "dialog.header",
  children,
  description,
  descriptionId,
  icon,
  infoIconTooltip,
  labelBack,
  labelClose,
  labelId,
  onClickBack,
  onClickClose,
  preventClose = false,
  ...props
}: DialogHeaderProps) {
  const modalContext = useModalContext();
  const t = useI18n();
  const internalLabelId = useId();
  const internalDescriptionId = useId();

  const labelIdDefault = labelId || modalContext.labelId || internalLabelId;
  const descriptionIdDefault =
    descriptionId || modalContext.descId || internalDescriptionId;
  const labelCloseDefault = t("dialog.header.close");
  const labelBackDefault = t("dialog.header.back");

  return (
    <div
      className={classNames(
        "flex",
        "flex_row",
        "justify_between",
        "p_xl",
        "pl_xxl",
        "shrink_0",
        "items_center",
      )}
      data-testid={dataTestId}
      {...props}
    >
      <div
        className={classNames(
          "flex",
          "flex_col",
          "justify_center",
          "gap_s",
          "overflow_hidden",
        )}
        data-testid={`${dataTestId}.title`}
      >
        <div
          className={classNames(
            "flex",
            "flex_row",
            "items_center",
            "gap_m",
            "flex_wrap",
          )}
        >
          <span
            className={classNames("flex", "flex_row", "items_center", "gap_m")}
          >
            {icon ? (
              <span
                className={classNames(
                  "shrink_0",
                  "pr_m",
                  "flex",
                  "items_center",
                  "justify_center",
                )}
              >
                {icon}
              </span>
            ) : null}

            {onClickBack || labelBack ? (
              <IconButton
                variant="quiet"
                icon={
                  <DirectionLeftIcon
                    className={classNames("rtl_rotate_y_180")}
                  />
                }
                onClick={onClickBack}
                label={labelBack || labelBackDefault}
              />
            ) : null}

            {typeof children === "string" ? (
              <span
                className={classNames(
                  "text_default",
                  "font_label_m_emphasized",
                  "break_words",
                )}
                id={labelIdDefault}
              >
                {children}
              </span>
            ) : (
              children
            )}
          </span>
          {infoIconTooltip ? (
            <InfoTooltipPrimitive
              title={infoIconTooltip}
              aria-labelledby={labelIdDefault}
            />
          ) : null}
        </div>
        {description && typeof description === "string" ? (
          <span
            className={classNames("font_label_s", "text_weak", "break_words")}
            id={descriptionIdDefault}
            data-testid={`${dataTestId}.description`}
          >
            {description}
          </span>
        ) : (
          description
        )}
      </div>
      {!preventClose && (
        <span className={classNames("self_start")}>
          <IconButton
            variant="quiet"
            icon={<CrossOutline />}
            onClick={onClickClose}
            label={labelClose || labelCloseDefault}
            data-testid={`${dataTestId}.close`}
          />
        </span>
      )}
    </div>
  );
}

import {
  type CSSProperties,
  type DragEvent,
  type ReactNode,
  type Ref,
  forwardRef,
} from "react";

import { useControl } from "@qlik/sprout-css-hooks";

import { Button, type ButtonProps } from "../Button";
import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";

import styles from "./Dropzone.module.css";

type Status = "dragging" | "hover";

export type DropzoneProps = {
  disabled?: boolean;
  status?: Status;
  statusDefault?: Status;
  onStatusChange?: (status: Status) => void;
  icon?: ReactNode;
  title?: ReactNode;
  description?: ReactNode;
  button?: Partial<ButtonProps>;
  secondaryButton?: Partial<ButtonProps>;
  onDragOver?: (e: DragEvent<HTMLDivElement>) => void;
  width?: CSSProperties["width"];
} & HTMLDivProps;

export const Dropzone = forwardRef<HTMLDivElement, DropzoneProps>(DropzoneBase);

function DropzoneBase(
  {
    icon,
    title,
    disabled,
    status,
    statusDefault,
    children,
    onStatusChange,
    description,
    button,
    secondaryButton,
    onDragEnter,
    onDragOver,
    onDragLeave,
    onDrop,
    ...props
  }: DropzoneProps,
  ref?: Ref<HTMLDivElement>,
) {
  const controlled = useControl<Status>(
    {
      status,
      statusDefault,
      onStatusChange,
    },
    {
      onChangeKey: "onStatusChange",
      valueKey: "status",
      defaultValueKey: "statusDefault",
      selector: (e) => e,
    },
  );
  const dataState = controlled.value || (disabled && "disabled") || "enabled";

  return (
    <div
      {...props}
      ref={ref}
      tabIndex={disabled ? -1 : 0}
      data-state={dataState}
      aria-disabled={disabled ? "true" : "false"}
      className={classNames(
        "flex_noreset",
        "border_box",
        "flex_col",
        "items_center",
        "radius_subtle",
        "w_full",
        "gap_m",
        {
          [styles.container]: true,
        },
      )}
      onDragOver={(e) => {
        controlled.onChange("dragging");
        onDragOver?.(e);
      }}
      onDragEnter={(e) => {
        controlled.onChange("dragging");
        onDragEnter?.(e);
      }}
      onDragLeave={(e) => {
        controlled.onChange();
        onDragLeave?.(e);
      }}
      onDrop={(e) => {
        controlled.onChange();
        onDrop?.(e);
      }}
    >
      {children}
      <div
        className={classNames(
          "flex_noreset",
          "flex_col",
          "gap_xl",
          "w_full",
          "items_center",
          "justify_center",
        )}
      >
        {icon ? (
          <div
            className={classNames("size_xxl", "icon_size_xxl", {
              text_default: !disabled,
              text_disabled: !!disabled,
            })}
          >
            {icon}
          </div>
        ) : null}
        {title || description ? (
          <div
            className={classNames(
              "flex_noreset",
              "flex_col",
              "items_center",
              "gap_s",
            )}
          >
            {title ? (
              <span
                className={classNames("font_label_s_emphasized", {
                  text_default: !disabled,
                  text_disabled: !!disabled,
                })}
              >
                {title}
              </span>
            ) : null}
            {description ? (
              <span
                className={classNames("font_label_xs", {
                  text_weak: !disabled,
                  text_disabled: !!disabled,
                })}
              >
                {description}
              </span>
            ) : null}
          </div>
        ) : null}
        {button || secondaryButton ? (
          <div className={classNames("flex_noreset", "flex_row", "gap_m")}>
            {secondaryButton ? (
              <Button disabled={disabled} {...secondaryButton} />
            ) : null}
            {button ? <Button disabled={disabled} {...button} /> : null}
          </div>
        ) : null}
      </div>
    </div>
  );
}

DropzoneBase.displayName = "Dropzone";

