import { useEffect, useRef, useState } from "react";

import { Button } from "../Button";
import { classNames } from "../classNames";
import {
  FloatingPrimitive,
  type FloatingPrimitiveProps,
} from "./FloatingPrimitive";

export function FloatingPrimitiveAnchorEl({
  wrapBtn,
  open,
  onOpenChange,
  ...props
}: FloatingPrimitiveProps & { wrapBtn?: boolean }) {
  const [safeOpen, setOpen] = useState(false);
  useEffect(() => {
    setOpen(!!open);
  }, [open]);
  const ref = useRef<HTMLButtonElement | null>(null);
  let btn = (
    <button
      ref={ref}
      onClick={() => {
        setOpen(true);
      }}
    >
      Click me
    </button>
  );
  // create a corner case to test if ref update works
  if (wrapBtn) {
    btn = (
      <div style={{ inlineSize: 100, blockSize: 100, padding: 20 }}>{btn}</div>
    );
  }
  return (
    <div>
      {btn}
      <FloatingPrimitive
        anchorEl={ref.current || undefined}
        open={safeOpen}
        onOpenChange={(value) => {
          if (onOpenChange) {
            onOpenChange(value);
          }
          if (!value) {
            setOpen(false);
          }
        }}
        {...props}
      />
    </div>
  );
}

export function MultipleFloatingPrimites({
  disableScrollLock,
  hideBackdrop,
}: Pick<FloatingPrimitiveProps, "disableScrollLock" | "hideBackdrop">) {
  return (
    <div className={classNames("flex", "gap_3xl")}>
      <FloatingPrimitive
        disableScrollLock={disableScrollLock}
        hideBackdrop={hideBackdrop}
        initialOpen
        content="Alpha"
        data-testid="fp-1"
      >
        <Button label="Open Alpha" />
      </FloatingPrimitive>
      <FloatingPrimitive
        disableScrollLock={disableScrollLock}
        hideBackdrop={hideBackdrop}
        initialOpen
        content="Beta"
        data-testid="fp-2"
      >
        <Button label="Open Beta" />
      </FloatingPrimitive>
      <FloatingPrimitive
        disableScrollLock={disableScrollLock}
        hideBackdrop={hideBackdrop}
        initialOpen
        content="Gamma"
        data-testid="fp-3"
      >
        <Button label="Open Gamma" />
      </FloatingPrimitive>
      <FloatingPrimitive
        disableScrollLock={disableScrollLock}
        hideBackdrop={hideBackdrop}
        initialOpen
        content="Delta"
        data-testid="fp-4"
      >
        <Button label="Open Delta" />
      </FloatingPrimitive>
    </div>
  );
}

import type { CSSProperties } from "react";

export type WidthStrategy =
  | "reference"
  | "xxs"
  | "xs"
  | "s"
  | "m"
  | "l"
  | "xl"
  | "screen"
  | CSSProperties["width"];

export function getWidth(
  width: WidthStrategy,
  referenceWidth: number,
): CSSProperties["width"] {
  switch (width) {
    case "reference":
      return `${referenceWidth}px`;
    case "screen":
      return "calc(100vw - var(--sprout-common-spacing-m) * 2)";
    case "xxs":
      return "var(--sprout-container-sizing-xxs)";
    case "xs":
      return "var(--sprout-container-sizing-xs)";
    case "s":
      return "var(--sprout-container-sizing-s)";
    case "m":
      return "var(--sprout-container-sizing-m)";
    case "l":
      return "var(--sprout-container-sizing-l)";
    case "xl":
      return "var(--sprout-container-sizing-xl)";
    default:
      return width;
  }
}

