              Orange
            </Select.Option>
          </Select.Select>
          <Switch
            data-testid="switch-light"
            label="Light"
            {...register("light")}
          />
          <Select.Multi
            data-testid="select-vegetables"
            label="Vegetables"
            placeholder="Select vegetables"
            labelClear="Clear input"
            {...register("vegetables")}
          >
            <Select.OptGroup label="Legumes">
              <Select.Option value="beans" data-testid="select-beans">
                Beans
              </Select.Option>
              <Select.Option value="peas" data-testid="select-peas">
                Peas
              </Select.Option>
              <Select.Option value="lentils" data-testid="select-lentils">
                Lentils
              </Select.Option>
            </Select.OptGroup>
          </Select.Multi>
          <Button type="submit" label="Submit" variant="primary" />
        </div>
      </form>
    </>
  );
}

import {
  type ComponentPropsWithoutRef,
  type ForwardedRef,
  type ReactNode,
  forwardRef,
} from "react";

import { useMedia } from "@qlik/sprout-css-hooks";
import type { ViewportSize } from "@qlik/sprout-css-modules";
import { classNames } from "@qlik/sprout-css-react";

type ColumnClass =
  | "col_span_1"
  | "col_span_2"
  | "col_span_3"
  | "col_span_4"
  | "col_span_5"
  | "col_span_6"
  | "col_span_7"
  | "col_span_8"
  | "col_span_9"
  | "col_span_10"
  | "col_span_11"
  | "col_span_12"
  | "col_span_13"
  | "col_span_14"
  | "col_span_15"
  | "col_span_16";

type ColumnProps = Partial<Record<ViewportSize, ColumnClass>> & {
  children?: ReactNode;
} & ComponentPropsWithoutRef<"div">;

type ContainerProps = ComponentPropsWithoutRef<"div"> & {
  gap?: "responsive" | "none";
  margin?: "responsive" | "none";
};

function Container(
  {
    children,
    gap = "responsive",
    margin = "none",
    className,
    ...props
  }: ContainerProps,
  ref: ForwardedRef<HTMLDivElement>,
) {
  const media = useMedia();
  const gapClass =
    gap === "responsive" &&
    media.classNames({
      s: "gap_xl",
      m: "gap_xl",
      l: "gap_xxl",
      xl: "gap_3xl",
    });
  const marginClass =
    margin === "responsive" &&
    media.classNames({
      s: "m_xl",
      m: "m_xxl",
      l: "m_xxl",
      xl: "m_3xl",
    });
  return (
    <div
      ref={ref}
      className={classNames("grid", "grid_cols_16", {
        [className || ""]: !!className,
        [gapClass || ""]: !!gapClass,
        [marginClass || ""]: !!marginClass,
      })}
      {...props}
    >
      {children}
    </div>
  );
}

function Column(
  { s, m, l, xl, className, children, ...props }: ColumnProps,
  ref: ForwardedRef<HTMLDivElement>,
) {
  const media = useMedia();

  return (
    <div
      ref={ref}
      className={media.classNames(
        {
          s,
          m,
          l,
          xl,
        },
        className,
      )}
      {...props}
    >
      {children}
    </div>
  );
}

export type GridProps = {
  Container: ContainerProps;
  Column: ColumnProps;
};

export const Grid = {
  Container: forwardRef<HTMLDivElement, ContainerProps>(Container),
  Column: forwardRef<HTMLDivElement, ColumnProps>(Column),
};

import type { ReactNode } from "react";

import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";

import styles from "./HelperText.module.css";

export type HelperTextProps = Omit<HTMLDivProps, "children"> & {
  isError?: boolean;
  variant?: "default" | "quiet";
  helpText: ReactNode;
  disabled?: boolean;
};

export function HelperText({
  disabled,
  isError,
  variant = "default",
  helpText,
  ...props
}: HelperTextProps) {
  return (
    <div
      {...props}
      className={classNames(
        "font_label_xs",
        "text_wrap",
        "break_words",
        styles.helper,
        {
          text_default: variant === "default" && !isError,
          text_danger: !!isError,
          text_weak: variant === "quiet" && !isError && !disabled,
          text_disabled: !!disabled,
        },
      )}
    >
      {helpText}
    </div>
  );
}

