import type React from "react";
import {
  type ChangeEventHandler,
  type Ref,
  forwardRef,
  useEffect,
  useState,
} from "react";

import { useControl } from "@qlik/sprout-css-hooks";

import type { ChangeReasons } from "../Input";
import { Select, type SelectProps } from "../Select";
import type { ChangeEvent } from "../Select/SelectUtils";
import { getSelectedOption } from "../Select/getSelectedOption";
import { classNames } from "../classNames";
import {
  InlineEditPrimitive,
  type InlineEditPrimitiveProps,
} from "./InlineEditPrimitive";
import { Placeholder } from "./Placeholder";

export type InlineEditSelectProps = Omit<
  InlineEditPrimitiveProps,
  "view" | "edit"
> & {
  placeholder?: SelectProps["Select"]["placeholder"];
  value?: SelectProps["Select"]["value"];
  children: React.ReactNode;
  defaultValue?: SelectProps["Select"]["defaultValue"];
  onChange?: (
    e: Parameters<ChangeEventHandler<HTMLInputElement>>[0],
    reason?: ChangeReasons | "cancel",
  ) => void;
  hasError?: SelectProps["Select"]["hasError"];
  errorMessages?: SelectProps["Select"]["errorMessages"];
};

export const InlineEditSelect = forwardRef<
  HTMLDivElement,
  InlineEditSelectProps
>(InlineEditSelectBase);

function InlineEditSelectBase(
  {
    "aria-label": ariaLabel,
    placeholder,
    isEditingDefault,
    isEditing,
    errorMessages,
    onSave,
    onCancel,
    showFormButtons,
    defaultValue,
    value,
    children,
    onChange,
    hasError,
    ...props
  }: InlineEditSelectProps,
  ref?: Ref<HTMLDivElement>,
) {
  const controlled = useControl<string>(
    {
      value,
      onChange,
      defaultValue,
    },
    {
      onChangeKey: "onChange",
      valueKey: "value",
      defaultValueKey: "defaultValue",
      selector: (e) => e?.target?.value,
    },
  );
  const [selectedProps, setSelectedProps] = useState<SelectProps["Option"]>({
    children: "",
  });
  useEffect(() => {
    const selected = getSelectedOption(children, controlled.value);
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
    if (selected) {
      // eslint-disable-next-line react-hooks/set-state-in-effect
      setSelectedProps(selected as SelectProps["Option"]);
    }
  }, [children, controlled.value]);
  return (
    <InlineEditPrimitive
      aria-label={ariaLabel}
      ref={ref}
      editFocusSelector={`[role="combobox"]`}
      isEditingDefault={isEditingDefault}
      isEditing={isEditing}
      onSave={onSave}
      onCancel={() => {
        onCancel?.();
        const myevent: ChangeEvent = {
          type: "change",
          target: {
            type: "select-one",
            value: defaultValue || value || "",
            selectedOptions: [],
          },
        };
        controlled.onChange(myevent, "cancel");
      }}
      showFormButtons={showFormButtons}
      errorMessages={errorMessages}
      {...props}
      view={
        <div
          className={classNames(
            "overflow_hidden",
            "w_full",
            "break_words",
            "text_default",
            "text_start",
          )}
        >
          {selectedProps.children || controlled.value || (
            <Placeholder>{placeholder || ""}</Placeholder>
          )}
        </div>
      }
      edit={
        <Select.Select
          hideBackdrop
          aria-label={ariaLabel}
          defaultOpen
          placeholder={placeholder}
          value={controlled.value}
          onChange={(e) => {
            controlled.onChange(e);
          }}
          hasError={hasError}
        >
          {children}
        </Select.Select>
      }
    />
  );
}

InlineEditSelectBase.displayName = "InlineEdit.Select";

import {
  type ChangeEventHandler,
  type JSX,
  type ReactNode,
  type Ref,
  forwardRef,
} from "react";

import { useControl } from "@qlik/sprout-css-hooks";

import { type ChangeReasons, TextField, type TextFieldProps } from "../Input";
import type { PossibleValues } from "../Typography";
import { classNames } from "../classNames";
import {
  InlineEditPrimitive,
  type InlineEditPrimitiveProps,
} from "./InlineEditPrimitive";
import { Placeholder } from "./Placeholder";

export type TextFieldViewProps = {
  children?: ReactNode;
  font?: PossibleValues["font"];
};

function TextFieldView({ children, font }: TextFieldViewProps): JSX.Element {
  return (
    <span
      className={classNames(
        `font_${font || "label_s"}`,
        `text_default`,
        `text_start`,
        "ellipsis",
      )}
      // Setting inline style to override `white-space: nowrap` in `ellipsis` class
      style={{ whiteSpace: "pre" }}
    >
      {children}
    </span>
  );
}

export type InlineEditTextFieldProps = Omit<
  InlineEditPrimitiveProps,
  "view" | "edit"
> & {
  type?: TextFieldProps["type"];
  placeholder?: TextFieldProps["placeholder"];
  value?: TextFieldProps["value"];
  font?: PossibleValues["font"];
  view?: (props: TextFieldViewProps) => JSX.Element;
  defaultValue?: TextFieldProps["defaultValue"];
  onChange?: (
    e: Parameters<ChangeEventHandler<HTMLInputElement>>[0],
    reason?: ChangeReasons | "cancel",
  ) => void;
  hasError?: TextFieldProps["hasError"];
  errorMessages?: TextFieldProps["errorMessages"];
};

export const InlineEditTextField = forwardRef<
  HTMLDivElement,
  InlineEditTextFieldProps
>(InlineEditTextFieldBase);

function InlineEditTextFieldBase(
  {
    "aria-label": ariaLabel,
    "aria-labelledby": ariaLabelledBy,
    placeholder,
    isEditingDefault,
    isEditing,
    errorMessages,
    onSave,
    onCancel,
    hasError,
    showFormButtons,
    defaultValue,
    value,
    view = TextFieldView,
    font,
    type,
    onChange,
    ...props
  }: InlineEditTextFieldProps,
  ref?: Ref<HTMLDivElement>,
) {
  const controlled = useControl<string>(
    {
      value,
      defaultValue,
      onChange,
    },
    {
      valueKey: "value",
      defaultValueKey: "defaultValue",
      onChangeKey: "onChange",
      selector: (e) => (e.target as HTMLInputElement).value,
    },
  );

  const View = view;

  const viewNode = (
    <View font={font}>
      {controlled.value || <Placeholder>{placeholder}</Placeholder>}
    </View>
  );

  return (
    <InlineEditPrimitive
      aria-label={ariaLabel}
      aria-labelledby={ariaLabelledBy}
      ref={ref}
      isEditingDefault={isEditingDefault}
      isEditing={isEditing}
      onSave={onSave}
      onCancel={() => {
        onCancel?.();
        controlled.onChange(
          { target: { value: defaultValue || value } },
          "cancel",
        );
      }}
      showFormButtons={showFormButtons}
      errorMessages={errorMessages}
      {...props}
      view={viewNode}
      edit={
        <TextField
          aria-label={ariaLabel}
          aria-labelledby={ariaLabelledBy}
          placeholder={placeholder}
          value={controlled.value}
          font={font || "label_s"}
          hasError={hasError}
          type={type}
          onChange={(e, r) => {
            controlled.onChange(e, r);
          }}
        />
      }
    />
  );
}

InlineEditTextFieldBase.displayName = "InlineEdit.TextField";

import type { ReactNode } from "react";

import { classNames } from "../classNames";

export function Placeholder({ children }: { children: ReactNode }) {
  return (
    <div className={classNames("font_label_s", "text_weak")}>{children}</div>
  );
}

import {
  type ReactElement,
  type ReactNode,
  cloneElement,
  isValidElement,
} from "react";

import sprout from "@qlik/sprout-css-modules";

import { Button } from "../Button/Button";
import type { HTMLSpanProps } from "../htmlTypes";

export type AffixProps = {
  label: string;
  onClick?: () => void;
  disabled?: boolean;
  icon?: ReactNode & { height?: number; width?: number };
};

export type AffixWrapperProps = {
  children: ReactNode;
} & HTMLSpanProps;

const affixWrapper = sprout.classNames("flex", "items_center", "m_s");
const affixContent = sprout.classNames("flex", "bg_moderate", "radius_pill");

export function AffixWrapper({ children, ...props }: { children: ReactNode }) {
  return (
    <span className={affixContent} {...props}>
      {children}
    </span>
  );
}

export function Affix(props: { affix: AffixProps | ReactNode }) {
  if (isValidElement(props.affix)) {
    return <span className={affixWrapper}>{props.affix}</span>;
  }
  const affix = props.affix as AffixProps;

  if (affix.icon && isValidElement(affix.icon)) {
    // fix usage of icon that may set height and width which break the visual in size small
    affix.icon = cloneElement(
      affix.icon as ReactElement<{ width?: string; height?: string }>,
      {
        height: undefined,
        width: undefined,
      },
    );
  }
  return (
    <span className={affixWrapper}>
      <AffixWrapper>
        <Button {...affix} variant="quiet" size="small" />
      </AffixWrapper>
    </span>
  );
}

import {
  type ChangeEvent,
  type ChangeEventHandler,
  type ReactNode,
  type Ref,
  forwardRef,
  useRef,
} from "react";

import ErrorOutlineIcon from "@qlik/sprout-icons/react/ErrorOutline";

import { InputButton } from "../InputButton";
import type { PossibleValues } from "../Typography";
import { mergeRefs } from "../Utils/mergeRef";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import { useValueControl } from "../hooks/useValueControl";
import type { HTMLInputProps } from "../htmlTypes";
import { Affix, type AffixProps } from "./Affix";
import { SearchIcon } from "./SearchIcon";

import styles from "./Input.module.css";

export type ChangeReasons = "clear";

export type InputProps = Omit<HTMLInputProps, "onChange"> & {
  font?: PossibleValues["font"];
  labelClear?: string;
  leftAffix?: AffixProps | ReactNode;
  leftIcon?: ReactNode;
  rightAffix?: AffixProps | ReactNode;
  leftAdornment?: React.ReactNode;
  rightAdornment?: React.ReactNode;
  disabled?: boolean;
  readOnly?: boolean;
  hasError?: boolean;
  designSize?: "default" | "small";
  onChange?: (
    ev: Parameters<ChangeEventHandler<HTMLInputElement>>[0],
    reason?: ChangeReasons,
  ) => void;
};

const adornmentClassName = classNames(
  "font_label_s",
  "text_weak",
  "bg_transparent",
  "h_full",
  "flex",
  "justify_center",
  "items_center",
  "self_center",
);

/**
 * Firefox has agent styles that force every lib owner to fix the height of the input based on the line-height.
 */
function getFixedHeight(
  font: PossibleValues["font"],
  designSize?: "default" | "small",
) {
  const [type, size] = font.split("_");
  const lineHeight = `var(--sprout-${type}-font-${size}-line-height)`;
  return `calc(${lineHeight} - 2 * var(--sprout-common-border-default-width) + 2 * ${designSize === "small" ? "var(--sprout-common-spacing-s)" : "var(--sprout-common-spacing-m)"})`;
}

export const Input = forwardRef<HTMLInputElement, InputProps>(InputBase);

function InputBase(props: InputProps, ref?: Ref<HTMLInputElement>) {
  const {
    id,
    font,
    labelClear,
    leftAffix,
    leftIcon,
    rightAffix,
    leftAdornment,
    rightAdornment,
    readOnly = false,
    disabled = false,
    type,
    hasError,
    designSize,
    value,
    onChange,
    defaultValue,
    placeholder,
    ...rest
  } = props;

  const t = useI18n();
  const internalRef = useRef<HTMLInputElement>(null);
  const labelClearDefault = t("input.clear");
  const safeFont =
    designSize === "small" && !font ? "label_xs" : font || "label_s";
  const height = getFixedHeight(safeFont, designSize);

  const controlled = useValueControl<InputProps>({
    defaultValue,
    value,
    onChange,
  });

  let safeType = type || "text";
  if (type === "search") {
    safeType = "text";
  }

  let inputPlaceholder: string | undefined;
  if (typeof placeholder !== "undefined") {
    inputPlaceholder = placeholder;
  } else if (type === "search") {
    inputPlaceholder = "";
  } else {
    inputPlaceholder = undefined;
  }

  return (
    <div
      className={classNames(
        "flex",
        "border_box",
        "border_moderate",
        "radius_pill",
        "w_full",
        "flex_row",
        "items_center",
        "overflow_hidden",
        styles.field,
        {
          [styles.field_error]: !!hasError,
          [styles.field_disabled]: disabled,
          [styles.small]: designSize === "small",
        },
      )}
    >
      {leftAffix ? <Affix affix={leftAffix} /> : null}
      <div
        className={classNames(
          "w_full",
          "overflow_hidden",
          styles.fieldcontent,
          {
            gap_m: designSize !== "small",
            gap_s: designSize === "small",
          },
        )}
      >
        <div
          className={classNames(
            "w_full",
            "flex",
            "flex_row",
            "pr_s",
            "items_center",
            "gap_m",
            "text_default",
          )}
        >
          {leftAdornment ? (
            <span className={adornmentClassName}>{leftAdornment}</span>
          ) : null}
          {type === "search" ? <SearchIcon /> : leftIcon}
          <input
            {...rest}
            {...controlled}
            placeholder={inputPlaceholder}
            id={id}
            type={safeType}
            ref={mergeRefs([ref, internalRef])}
            disabled={disabled}
            readOnly={readOnly}
            className={classNames(
              "flex",
              "flex_row",
              "items_center",
              "m_0",
              "p_0",
              "bg_transparent",
              "border_box",
              "text_default",
              "flex_1",
              "border_none",
              "block",
              "w_full",
              "ellipsis",
              styles.input,
              {
                [`font_${safeFont}`]: !!safeFont,
              },
            )}
            aria-invalid={hasError ? "true" : undefined}
            data-size={designSize}
            style={{
              blockSize: height,
            }}
          />
          {rightAdornment ? (
            <span className={adornmentClassName}>{rightAdornment}</span>
          ) : null}
        </div>
        {hasError ? (
          <ErrorOutlineIcon
            data-icon="error"
            data-testid="input-error-icon"
            width={undefined}
            height={undefined}
          />
        ) : null}
      </div>
      {type === "search" && (
        <InputButton
          data-feature="input-search-clear"
          onClick={() => {
            if (internalRef.current) {
              internalRef.current.value = "";
            }
            if (controlled.onChange) {
              controlled.onChange(
                {
                  target: {
                    value: "",
                  },
                } as ChangeEvent<HTMLInputElement>,
                "clear",
              );
            }
          }}
          aria-label={labelClear || labelClearDefault}
          size={designSize === "small" ? "xs" : "s"}
        />
      )}
      {rightAffix ? <Affix affix={rightAffix} /> : null}
    </div>
  );
}

InputBase.displayName = "Input";

