import { MenuItem, type MenuItemProps } from "./MenuItem";
import { MenuTriggerBase, type MenuTriggerBaseProps } from "./MenuTriggerBase";

type MenuItemstructure = {
  component: "item";
} & MenuItemProps;

type MenuDividerStructure = {
  component: "divider";
};

type MenuGroupStructure = {
  component: "group";
  menu: Array<MenuRowStruct>;
} & Omit<MenuGroupLabelProps, "children">;

type MenuGroupLabelStructure = {
  component: "grouplabel";
} & Omit<MenuGroupLabelProps, "children">;

type MenuSubProps = Omit<MenuTriggerBaseProps, "children" | "menu"> &
  MenuItemProps;
type MenuRowStruct =
  | MenuGroupStructure
  | MenuItemstructure
  | MenuDividerStructure
  | MenuGroupLabelStructure
  | ({
      component: "sub";
      menu: Array<MenuRowStruct>;
    } & MenuSubProps);

type MenuDataStructure = Array<MenuRowStruct>;

export type MenuRendererProps = { menu: MenuDataStructure | ReactNode };

export function MenuRenderer({ menu }: MenuRendererProps) {
  const isRTL = document.dir === "rtl";
  if (isValidElement(menu)) {
    return menu as ReactNode;
  }
  // trick a bit the type system
  const safeMenu = menu as MenuDataStructure;
  return safeMenu.map((item, index) => {
    if (item.component === "item") {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const { component, ...props } = item;
      return <MenuItem key={index} {...props} />;
    }
    if (item.component === "divider") {
      return <MenuDivider key={index} />;
    }
    if (item.component === "grouplabel") {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const { component, ...props } = item;
      return <MenuGroupLabel key={index} {...props} />;
    }
    if (item.component === "group") {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const { component, menu, ...props } = item;
      return (
        <MenuGroup key={index} {...props}>
          <MenuRenderer menu={menu} />
        </MenuGroup>
      );
    }
    if (item.component === "sub") {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const { component, menu, ...props } = item;
      return (
        <MenuTriggerBase
          key={index}
          menu={<MenuRenderer menu={menu} />}
          placement={isRTL ? "left" : "right"}
        >
          <MenuItem {...props} isNested />
        </MenuTriggerBase>
      );
    }
  });
}

MenuRenderer.displayName = "Menu.Renderer";

import { Skeleton } from "../Skeleton";
import { classNames } from "../classNames";

export function MenuSkeleton() {
  return (
    <div className={classNames("flex", "flex_col", "border_box", "gap_m")}>
      <Skeleton variant="rectangle" width="150px" height="22px" />
      <Skeleton variant="rectangle" width="100px" height="22px" />
      <Skeleton variant="rectangle" width="200px" height="22px" />
    </div>
  );
}

import type { ReactNode } from "react";

import { classNames } from "../classNames";

export type MenuSplitProps = {
  children: ReactNode;
};

export function MenuSplit({ children }: MenuSplitProps) {
  return (
    <div className={classNames("flex", "flex_row", "gap_0")}>{children}</div>
  );
}

import { forwardRef, useEffect, useRef, useState } from "react";

import { Button } from "../Button";
import { TextField } from "../Input";
import { Textarea } from "../Textarea";
import { classNames } from "../classNames";
import { Menu, type MenuProps } from "./Menu";

// Enforce we have forwardRef in the Menu.Trigger
const ForwardRefTest = forwardRef<HTMLDivElement, MenuProps["Trigger"]>(
  (props, ref) => <Menu.Trigger ref={ref} {...props} />,
);
ForwardRefTest.displayName = "ForwardRefTest";

export function MenuFocusReturnTest() {
  const [displayForm, setDisplayForm] = useState(false);
  return (
    <div>
      {!displayForm ? (
        <div>
          <ForwardRefTest
            menu={
              <>
                <Menu.Item label="first" />
                <Menu.Item
                  label="display form"
                  onClick={() => {
                    setDisplayForm(true);
                  }}
                />
                <Menu.Item label="last" />
              </>
            }
          >
            <Button label="Open Menu" />
          </ForwardRefTest>
        </div>
      ) : (
        <form>
          <TextField label="name" autoFocus />
          <Textarea label="biography" />
        </form>
      )}
    </div>
  );
}

export function MenuRefTest() {
  const [editMode, setEditMode] = useState<boolean>(false);
  const [newValue, setNewValue] = useState<string>("value");
  const [restoreFocus, setRestoreFocus] = useState<boolean>(false);
  const actionBtnRef = useRef<HTMLButtonElement | null>(null);

  useEffect(() => {
    if (!editMode && restoreFocus) {
      actionBtnRef.current?.focus();
    }
  }, [editMode, restoreFocus]);

  return editMode ? (
    <input
      value={newValue}
      ref={(node) => {
        if (node) {
          node.focus();
        }
      }}
      onChange={(e) => {
        setNewValue(e.target.value);
      }}
      onKeyDown={(e) => {
        if (e.key === "Enter") {
          e.preventDefault();
          e.stopPropagation();
          setEditMode(false);
        }
      }}
    />
  ) : (
    <Menu.Trigger
      menu={
        <Menu.Item
          label="Edit"
          onClick={(e) => {
            e.stopPropagation();
            e.preventDefault();
            setEditMode(true);
            setRestoreFocus(true);
          }}
        />
      }
    >
      <Button ref={actionBtnRef} variant="quiet" label="Menu" />
    </Menu.Trigger>
  );
}

export function MenuControlledOpenState() {
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen((prevOpen) => !prevOpen);
  };

  return (
    <div id="component-testing">
      <Button label="External trigger" onClick={handleOpen} />

      <Menu.Trigger
        open={open}
        menu={
          <>
            <Menu.Item icon={null} label="Profile" />
            <Menu.Item icon={null} label="Logout" />
          </>
        }
      >
        <Button label="Trigger" onClick={handleOpen} />
      </Menu.Trigger>
    </div>
  );
}

export function MenuControlledOpenChange() {
  const [callCount, setCallCount] = useState(0);

  const handleOpenChange = () => {
    setCallCount((c) => c + 1);
  };

  return (
    <div id="component-testing">
      <Button
        label="External trigger"
        onClick={() => {
          handleOpenChange();
        }}
      />
      <p className={classNames("font_body_m")}>
        onChange has been called:{" "}
        <span data-testid="call-count">{callCount}</span>
      </p>

      <Menu.Trigger
        onOpenChange={handleOpenChange}
        menu={
          <>
            <Menu.Item icon={null} label="Profile" />
            <Menu.Item icon={null} label="Logout" />
          </>
        }
      >
        <Button label="Trigger" />
      </Menu.Trigger>
    </div>
  );
}

export function MenuBackdropTest() {
  return (
    <div id="component-testing">
      <Menu.Trigger
        menu={
          <>
            <Menu.Item icon={null} label="Profile" />
            <Menu.Item icon={null} label="Logout" />
          </>
        }
      >
        <Button label="Open Menu" />
      </Menu.Trigger>
      <Button label="External Button" />
    </div>
  );
}

import { expect, test } from "@playwright/experimental-ct-react";

import { getAxeReport } from "../PlaywrightUtils";
import { Message } from "./Message";

test("should be accessible", async ({ mount, page }) => {
  const component = await mount(
    <Message
      id="component-testing"
      title="Hello"
      message="World"
      actions={<button>Close</button>}
    />,
  );

  // axe core
  const accessibilityScanResults = await getAxeReport(page);
  expect(accessibilityScanResults.violations).toEqual([]);
  await expect(component).not.toHaveAttribute("role", "status");
  await expect(component).not.toHaveAttribute("aria-live", "polite");
  await component.update(
    <Message
      title="Please wait"
      message="we are working hard for you"
      progress={{ "aria-label": "wait", percent: 50 }}
    />,
  );
  await expect(component).toHaveAttribute("role", "status");
  await expect(component).toHaveAttribute("aria-live", "polite");
});

test("should set custom data-testid", async ({ mount }) => {
  const component = await mount(
    <Message
      title="Hello"
      message="World"
      actions={<button>Close</button>}
      data-testid="foo"
    />,
  );

  await expect(component.getByTestId("foo.message-title")).toHaveText("Hello");
  await expect(component.getByTestId("foo.message-description")).toHaveText(
    "World",
  );
  await expect(component.getByTestId("foo.message-actions")).toContainText(
    "Close",
  );
});

