export function AnchorPositionAPI() {
  const [clickPos, setClickPos] = useState<
    PopoverProps["anchorPosition"] | undefined
  >();
  const [open, setOpen] = useState(false);
  return (
    <div
      className={classNames("flex", "flex_col", "gap_xl")}
      style={{ inlineSize: "400px" }}
    >
      <Menu.Trigger
        menu={
          <Menu.Item
            label="Delete"
            onClick={(e) => {
              setClickPos({
                top: e.clientY,
                left: e.clientX,
              });
              setOpen(!open);
            }}
          />
        }
      >
        <Button label="OpenMenu" asDropdown />
      </Menu.Trigger>
      <Popover
        anchorPosition={clickPos}
        p="m"
        open={open}
        onOpenChange={setOpen}
        content={
          <form
            className={classNames(
              "flex",
              "flex_col",
              "gap_m",
              "items_start",
              "bg_default",
            )}
            onSubmit={(e) => {
              e.preventDefault();
            }}
          >
            <h2
              className={classNames("flex", "text_default", "font_heading_s")}
            >
              Delete confirmation
            </h2>
            <AlertInline severity="warning">
              This action can t be reverted
            </AlertInline>
            <div
              className={classNames(
                "flex",
                "flex_col",
                "gap_m",
                "w_full",
                "items_end",
              )}
            >
