import {
  type JSX,
  type ChangeEvent as ReactChangeEvent,
  type ReactNode,
  type Ref,
  forwardRef,
  useCallback,
  useEffect,
  useRef,
  useState,
} from "react";
import { useMemo } from "react";

import { useControl } from "@qlik/sprout-css-hooks";
import sprout from "@qlik/sprout-css-modules";

import { type CommonFieldProps, Field, useFieldProps } from "../Field";
import {
  FloatingPrimitive,
  type FloatingPublicProps,
} from "../FloatingPrimitive";
import { Tag, TagGroups } from "../Tag";
import { ComboboxGesture } from "../Utils/gesture/combobox";
import {
  ComboboxEditable,
  type ComboboxPropsEditable,
} from "./SelectComboboxEditable";
import { SelectContext } from "./SelectContext";
import type { SelectOptionProps } from "./SelectOption";
import { type ChangeEvent, getAllItems, getSelectedItems } from "./SelectUtils";
import { getSelectedValueString } from "./getSelectedOption";

import menuStyle from "../Menu/Menu.module.css";

const noOpOnChange = () => {};

const createOnChangeEvent = ({
  name,
  value,
  values,
  action,
}: {
  name?: string;
  value?: string;
  values?: Array<string>;
  action?: string;
}) => {
  const output: ChangeEvent = {
    type: "change",
    target: {
      name,
      value,
      type: "select-multiple",
      selectedOptions: (values || []).map((v) => ({ value: v })),
    },
  };
  if (action === "add" && value) {
    // ensure it is inside it
    if (!(values || []).find((v) => v === value)) {
      output.target.selectedOptions = output.target.selectedOptions.concat({
        value,
      });
    }
  } else if (action === "remove") {
    output.target.selectedOptions = output.target.selectedOptions.filter(
      (v) => v.value !== value,
    );
  }
  return output;
};

export type MultiSelectProps = {
  name?: string;
  disabled?: boolean;
  placeholder?: string;
  id?: string;
  value?: Array<string>;
  defaultValue?: Array<string>;
  onChange?: (e: ChangeEvent) => void;
  filterValue?: string;
  onFilterChange?: (e: ReactChangeEvent<HTMLInputElement>) => void;
  children: ReactNode;
  "data-testid"?: string;
  defaultOpen?: boolean;
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
  hasError?: boolean;
  renderValue?: (
    value: Array<string>,
    selectedOptions: Record<string, SelectOptionProps>,
    input: ReactNode,
  ) => JSX.Element;
} & Pick<ComboboxPropsEditable, "labelClear" | "readOnly"> &
  CommonFieldProps & {
    /** @deprecated please use errorMessages instead */
    isError?: boolean;
  } & Pick<FloatingPublicProps, "disableScrollLock" | "hideBackdrop">;

export const MultiSelect = forwardRef<HTMLSelectElement, MultiSelectProps>(
  MultiSelectBase,
);

function MultiSelectBase(
  {
    "data-testid": dataTestId,
    children,
    defaultOpen,
    defaultValue,
    disabled,
    disableScrollLock = false,
    filterValue,
    hideBackdrop = false,
    isError,
    name,
    onChange,
    onFilterChange,
    onOpenChange,
    open,
    placeholder,
    readOnly,
    renderValue,
    value,
    ...props
  }: MultiSelectProps,
  ref?: Ref<HTMLSelectElement>,
) {
  const { inputProps, fieldProps, useField } = useFieldProps(props);
  const [ariaActiveDescendant, setAriaActiveDescendant] = useState<
    string | undefined
  >("");
  const listboxId = `${inputProps.id}-listbox`;

  const filterControlled = useControl<string>(
    {
      value: filterValue,
      onChange: (event: ReactChangeEvent<HTMLInputElement>) => {
        onFilterChange?.(event);
        if (!isOpen && event.target.value) {
          setIsOpen(true);
        }
      },
    },
    {
      selector: (e) => e.target.value,
    },
  );

  const onChangeDelayed = (e: ChangeEvent) => {
    setTimeout(() => {
      // react-hook-form: we have to wait the ref to be updated before calling the onChange
      if (onChange) {
        onChange(e);
      }
    });
  };

  const controlled = useControl<Array<string>>(
    {
      value,
      onChange: onChangeDelayed,
      defaultValue,
    },
    {
      onChangeKey: "onChange",
      valueKey: "value",
      defaultValueKey: "defaultValue",
      selector: (e) =>
        e.target.selectedOptions.map((o: { value: string }) => o.value),
    },
  );

  const controlledOpen = useControl<boolean>(
    {
      open,
      onOpenChange,
      initialOpen: defaultOpen ?? false,
    },
    {
      defaultValueKey: "initialOpen",
      valueKey: "open",
      onChangeKey: "onOpenChange",
      selector: (v) => v,
    },
  );
  const isOpen = !!controlledOpen.value;
  const setIsOpen = controlledOpen.onChange;

  const refsFloating = useRef<HTMLDivElement>(null);
  const refsReference = useRef<HTMLInputElement>(null);

  const onComboboxChange = useCallback((btn?: HTMLElement) => {
    if (btn) {
      const id = btn.getAttribute("id");
      if (id) {
        setAriaActiveDescendant(id);
      }
    }
    (refsReference.current as HTMLElement).focus();
  }, []);

  const gesture = new ComboboxGesture({
    getAllItems,
    getSelectedItems,
    isOpen,
    onOpenChange: (newValue: boolean) => {
      setIsOpen(newValue);
    },
    onChange: onComboboxChange,
  });
  gesture.listbox = refsFloating.current || undefined;
  if (refsReference.current) {
    gesture.setElement(refsReference.current);
  }
  gesture.onChange = (btn?: HTMLElement) => {
    if (btn) {
      const id = btn.getAttribute("id");
      if (id) {
        setAriaActiveDescendant(id);
      }
    }
  };

  const [missingLabel, setMissingLabel] = useState<boolean>(true);

  // accessibility gesture
  useEffect(() => {
    if (refsReference.current) {
      if (isOpen) {
        (refsReference.current as HTMLElement).focus();
      }
    }

    if (isOpen && refsFloating.current) {
      if (controlled.value === undefined) {
        gesture.focusOnFirst();
      } else {
        gesture.focusOnFirstSelected();
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isOpen]);

  useEffect(() => {
    if (!missingLabel) {
      // eslint-disable-next-line @eslint-react/hooks-extra/no-direct-set-state-in-use-effect
      setMissingLabel(true);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [controlled.value]);

  useEffect(() => {
    const clearT = setTimeout(() => {
      gesture.resetFocus();
      // Focus on first selected item if any otherwise first option
      gesture.focusOnFirstSelected();
    }, 0);
    return () => {
      clearTimeout(clearT);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filterControlled.value]);

  const safeRenderValue = useCallback(
    (
      values: Array<string>,
      selectedProps: Record<string, SelectOptionProps>,
      input: ReactNode,
    ) => {
      if (renderValue) {
        return renderValue(values, selectedProps, input);
      }
      return (
        <TagGroups data-class="tag-groups">
          {values.map((newValue: string) => {
            const label: string = getSelectedValueString(
              selectedProps,
              newValue,
            );
            return (
              <Tag
                size="s"
                key={newValue}
                text={label}
                data-testid={
                  dataTestId
                    ? `${dataTestId}.combobox.tag.${newValue}`
                    : `combobox.tag.${newValue}`
                }
                onRemove={
                  !disabled && !readOnly
                    ? () => {
                        controlled.onChange(
                          createOnChangeEvent({
                            name,
                            value: newValue,
                            values,
                            action: "remove",
                          }),
                        );
                      }
                    : undefined
                }
              />
            );
          })}
          {input}
        </TagGroups>
      );
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [disabled, readOnly, renderValue],
  );
  const contextValue = useMemo(
    () => ({
      queryFilter: filterControlled.value || "",
      value: controlled.value,
    }),
    [filterControlled.value, controlled.value],
  );
  const renderOptions = (
    <SelectContext.Provider value={contextValue}>
      {children}
    </SelectContext.Provider>
  );

  const content = (
    <>
      <span className={sprout.sr_only}>
        <select
          multiple
          value={controlled.value}
          // provide onChange to avoid react warning
          onChange={noOpOnChange}
          name={name}
          ref={ref}
          tabIndex={-1}
          aria-hidden="true"
          className={sprout.hidden}
        >
          {controlled.value?.filter(Boolean).map((v) => (
            <option key={v} value={v} tabIndex={-1}>
              {v}
            </option>
          ))}
        </select>
      </span>
      <ComboboxEditable
        {...inputProps}
        renderValues={safeRenderValue}
        values={controlled.value}
        hasError={isError || inputProps.hasError}
        placeholder={controlled.value?.length ? undefined : placeholder}
        role="combobox"
        aria-autocomplete="list"
        aria-activedescendant={ariaActiveDescendant}
        aria-controls={listboxId}
        value={filterControlled.value || ""}
        onChange={filterControlled.onChange}
        autoComplete="off"
        disabled={disabled}
        readOnly={readOnly}
        ref={refsReference}
        onClick={() => {
          if (!isOpen) {
            setIsOpen(true);
          }
        }}
        onFocus={() => {
          if (readOnly) {
            return;
          }
          // On focus only open the list, don't close it otherwise dropdown click will not work
          if (!isOpen) {
            setIsOpen(true);
          }
        }}
        onKeyDownCapture={(event) => {
          if (readOnly) {
            return;
          }

          // here we do specific behavior to the multi select
          if (event.key === "ArrowDown" && !isOpen) {
            event.preventDefault();
            setIsOpen(true);
          } else if (event.key === "Escape") {
            if (isOpen) {
              event.preventDefault();
              event.stopPropagation();
              setIsOpen(false);
              filterControlled.onChange({
                target: {
                  value: "",
                },
              });
            }
          } else if (event.key === "Enter") {
            event.preventDefault();
            filterControlled.onChange({
              target: {
                value: "",
              },
            });

            const btn = gesture.getListInfo().focus;
            if (btn) {
              const newValue = btn.dataset.option;
              const selected = btn.getAttribute("aria-selected") === "true";
              // name is required for react-hook-form
              controlled.onChange(
                createOnChangeEvent({
                  name,
                  value: newValue,
                  values: controlled.value,
                  action: selected ? "remove" : "add",
                }),
              );
            } else {
              // difference with the Select: we don't want to remove the selected values
              document.body.focus();
            }
          }
          // here we call the common to all combobox gestures
          // eslint-disable-next-line @typescript-eslint/ban-ts-comment
          // @ts-ignore
          gesture.onKeyDownHandler(event as KeyboardEvent);
        }}
        onDropDown={() => {
          //Before closing the list, we clear the filter
          if (isOpen) {
            filterControlled.onChange({
              target: {
                value: "",
              },
            });
          }
          setIsOpen(!isOpen);
        }}
        onClear={
          controlled.value?.length
            ? () => {
                controlled.onChange(createOnChangeEvent({ name, values: [] }));
              }
            : undefined
        }
        data-testid={dataTestId ? `${dataTestId}.combobox` : "combobox"}
        aria-expanded={isOpen ? "true" : "false"}
      >
        {renderOptions}
      </ComboboxEditable>

      <FloatingPrimitive
        disableScrollLock={disableScrollLock}
        hideBackdrop={hideBackdrop}
        ref={refsFloating}
        className={menuStyle.menu}
        role="listbox"
        initialOpen={defaultOpen}
        open={isOpen}
        onOpenChange={(newOpen) => {
          setIsOpen(newOpen);
          if (!newOpen) {
            filterControlled.onChange({
              target: {
                value: "",
              },
            });
          }
        }}
        anchorEl={refsReference.current || undefined}
        forceMount
        minWidth="reference"
        minHeight={100}
        placementUpdate="dropdown"
        maxWidth="s"
        tabIndex={-1}
        data-testid={dataTestId ? `${dataTestId}.listbox` : "listbox"}
        onMouseDown={(event) => {
          if (event.button === 0) {
            event.preventDefault();
            event.stopPropagation();
            const target = event.target as HTMLElement;
            const btn = target.closest(
              "button[data-option]",
            ) as HTMLElement | null;
            if (btn) {
              const newValue = btn.dataset.option;
              const selected = btn.getAttribute("aria-selected") === "true";
              controlled.onChange(
                createOnChangeEvent({
                  name,
                  value: newValue,
                  values: controlled.value,
                  action: selected ? "remove" : "add",
                }),
              );
              filterControlled.onChange({
                target: {
                  value: "",
                },
              });
            }
          }
        }}
        aria-labelledby={inputProps.id}
        id={listboxId}
        content={renderOptions}
      />
    </>
  );
  if (useField) {
    return (
      <Field {...fieldProps} isError={isError || inputProps.hasError}>
        {content}
      </Field>
    );
  }
  return content;
}

