import { useContext, useEffect, useState } from "react";

import AddOutline from "@qlik/sprout-icons/react/AddOutline";

import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import { SelectContext } from "./SelectContext";
import { SelectOption, type SelectOptionProps } from "./SelectOption";

export type OptionCreateProps = {
  label?: string;
  isValid?: (value: string) => boolean;
} & Partial<SelectOptionProps>;

export function OptionCreate({ label, isValid, ...props }: OptionCreateProps) {
  const t = useI18n();
  const defaultLabel = t("select.createoption.label");
  const [display, setDisplay] = useState(false);
  const context = useContext(SelectContext);
  const queryFilter = context.queryFilter || "";

  useEffect(() => {
    const shouldOk = isValid ? isValid(queryFilter) : true;
    // eslint-disable-next-line @eslint-react/hooks-extra/no-direct-set-state-in-use-effect
    setDisplay(shouldOk && queryFilter.length >= 2);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [queryFilter]);

  if (!display) {
    return null;
  }

  return (
    <SelectOption
      {...props}
      selectable={false}
      data-create="true"
      value={context.queryFilter}
    >
      <div className={classNames("flex_noreset", "items_center", "gap_m")}>
        <AddOutline />
        <span
          className={classNames("ellipsis")}
          title={context.queryFilter || ""}
        >
          {`${label || defaultLabel} ${(context.queryFilter || "").trim()}`}
        </span>
        <span />
      </div>
    </SelectOption>
  );
}

import {
  // eslint-disable-next-line @typescript-eslint/no-redeclare
  type MouseEvent,
  type ReactNode,
  type Ref,
  forwardRef,
  useEffect,
  useState,
} from "react";

import sprout from "@qlik/sprout-css-modules";
import DropdownArrow from "@qlik/sprout-icons/react/DropdownArrow";
import ErrorOutlineIcon from "@qlik/sprout-icons/react/ErrorOutline";

import type { HTMLButtonProps } from "../htmlTypes";
import type { SelectOptionProps } from "./SelectOption";
import { getSelectedOption } from "./getSelectedOption";

import style from "./Select.module.css";

export type ComboboxProps = Omit<
  HTMLButtonProps,
  "type" | "children" | "size" | "label"
> & {
  iconElement?: SVGSVGElement;
  hasError?: boolean;
  readOnly?: boolean;
  isOpen?: boolean;
  size?: "default" | "small";
  "aria-controls": string;
  placeholder?: string;
  value?: string;
  label?: string;
  defaultValue?: string;
  renderValue?: (value: string, selectedProps?: SelectOptionProps) => ReactNode;
  children: ReactNode;
};

export const Combobox = forwardRef<HTMLButtonElement, ComboboxProps>(
  ComboboxBase,
);

function ComboboxBase(
  {
    size,
    hasError,
    readOnly,
    isOpen,
    "aria-controls": ariaControls,
    // label,
    // iconElement,
    placeholder,
    disabled,
    value,
    onClick,
    renderValue,
    children,
    ...props
  }: ComboboxProps,
  ref?: Ref<HTMLButtonElement>,
) {
  const [selectedProps, setSelectedProps] = useState<SelectOptionProps>({
    children: "",
  });
  useEffect(() => {
    const selected = getSelectedOption(children, value);
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
    if (selected) {
      // eslint-disable-next-line @eslint-react/hooks-extra/no-direct-set-state-in-use-effect
      setSelectedProps(selected as SelectOptionProps);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [children]);
  return (
    <button
      role="combobox"
      type="button"
      {...props}
      aria-readonly={readOnly ? "true" : "false"}
      disabled={disabled}
      aria-controls={ariaControls}
      aria-expanded={isOpen ? "true" : "false"}
      ref={ref}
      className={sprout.classNames(
        "flex_noreset",
        "border_box",
        "w_full",
        "m_0",
        "text_start",
        "gap_m",
        "justify_start",
        "border_moderate",
        "radius_pill",
        "outline_focus_visible_centered",
        style.input,
        {
          [style.small]: size === "small",
          cursor_pointer: !disabled,
          cursor_not_allowed: !!disabled,
          border_error: !!hasError,
          bg_interactive: !readOnly && !isOpen,
          bg_disabled: !!readOnly,
          bg_pressed: !!isOpen,
        },
      )}
      onClick={(e: MouseEvent<HTMLButtonElement>) => {
        if (!disabled && !readOnly && onClick) {
          onClick(e);
        }
        if (readOnly) {
          const label = e.currentTarget.querySelector(
            `[data-testid="selected-value"]`,
          ) as HTMLElement | undefined;
          if (label) {
            const range = document.createRange();
            const selection = window.getSelection();
            if (selection) {
              selection.removeAllRanges();
              range.selectNodeContents(label);
              selection.addRange(range);
            }
          }
        }
      }}
    >
      {!!value && !!renderValue && (
        <span
          data-testid="selected-value"
          className={sprout.classNames(
            "flex_1",
            "border_box",
            "ellipsis",
            "pr_m",
            "w_full",
            "text_default",
            "font_label_s",
            "pointer_events_none",
          )}
        >
          {renderValue(value, selectedProps)}
        </span>
      )}
      {!!value && !renderValue && (
        <div
          data-testid="selected-value"
          className={sprout.classNames(
            "overflow_hidden",
            "w_full",
            "ellipsis",
            "text_default",
            "font_label_s",
          )}
        >
          {selectedProps.children}
        </div>
      )}
      {!value && (
        <span
          className={sprout.classNames(
            "flex_noreset",
            "pr_m",
            "text_weak",
            "font_label_s",
            "w_full",
          )}
        >
          {placeholder}
        </span>
      )}
      {!!hasError && (
        <ErrorOutlineIcon
          data-icon="error"
          width={undefined}
          height={undefined}
          className={sprout.classNames(
            "shrink_0",
            "text_danger",
            "self_center",
            {
              size_xl: size !== "small",
              size_l: size === "small",
            },
          )}
        />
      )}
      <DropdownArrow
        className={sprout.classNames(
          "fill_default",
          "shrink_0",
          "self_center",
          {
            fill_disabled: !!disabled || !!readOnly,
          },
        )}
      />
    </button>
  );
}
ComboboxBase.displayName = "Combobox";

import {
  type JSX,
  type ReactNode,
  type Ref,
  forwardRef,
  useEffect,
  useRef,
  useState,
} from "react";

import { useControl } from "@qlik/sprout-css-hooks";
import sprout from "@qlik/sprout-css-modules";
import { ErrorOutlineIcon } from "@qlik/sprout-icons/react";
import DropdownArrow from "@qlik/sprout-icons/react/DropdownArrow";

import { InputButton } from "../InputButton";
import { Tooltip } from "../Tooltip";
import { useI18n } from "../hooks/useI18n";
import type { HTMLInputProps } from "../htmlTypes";
import type { SelectOptionProps } from "./SelectOption";
import { getSelectedOptions } from "./getSelectedOption";

import multiStyles from "./MultiSelect.module.css";

type ComboboxOptionChild = {
  type: { displayName: string };
  props: SelectOptionProps;
};
export type ComboboxOptionsChildren = Array<ComboboxOptionChild>;

export type ComboboxPropsEditable = Omit<HTMLInputProps, "children"> & {
  renderValues: (
    value: Array<string>,
    selectedProps: Record<string, SelectOptionProps>,
    filterInput: ReactNode,
  ) => JSX.Element;
  children: ReactNode;
  values?: Array<string>;
  disabled?: boolean;
  readOnly?: boolean;
  hasError?: boolean;
  onDropDown?: () => void;
  onClear?: () => void;
  labelClear?: string;
  "data-testid"?: string;
};

export const ComboboxEditable = forwardRef<
  HTMLDivElement,
  ComboboxPropsEditable
>(ComboboxEditableBase);

function ComboboxEditableBase(
  props: ComboboxPropsEditable,
  ref?: Ref<HTMLDivElement>,
) {
  const {
    id,
    type,
    values,
    renderValues,
    children,
    disabled,
    readOnly,
    hasError,
    value,
    onChange,
    defaultValue,
    onDropDown,
    onClear,
    labelClear,
    "data-testid": dataTestId,
    "aria-labelledby": ariaLabelledBy,
    "aria-label": ariaLabel,
    ...rest
  } = props;
  const t = useI18n();
  const labelClearDefault = t("select.comboboxeditable.clear");
  const inputRef = useRef<HTMLInputElement | null>(null);
  const controlled = useControl<string>(
    {
      value,
      onChange,
      defaultValue,
    },
    {
      onChangeKey: "onChange",
      valueKey: "value",
      defaultValueKey: "defaultValue",
      selector: (e) => e.target.value,
    },
  );
  const [selectedProps, setSelectedProps] = useState<
    Record<string, SelectOptionProps>
  >({});
  useEffect(() => {
    const selected = getSelectedOptions(children, values);
    // eslint-disable-next-line @eslint-react/hooks-extra/no-direct-set-state-in-use-effect
    setSelectedProps(selected);
  }, [children, values]);
  const filterInput =
    (!readOnly && values) || !values ? (
      <input
        {...rest}
        aria-label={ariaLabel}
        id={id}
        value={controlled.value}
        onChange={controlled.onChange}
        type={type || "text"}
        disabled={disabled || readOnly}
        readOnly={readOnly}
        className={sprout.classNames(
          "relative",
          "flex_noreset",
          "flex_1",
          "border_none",
          "bg_transparent",
          "text_default",
          "font_label_s",
          "py_s",
          "px_xs",
          "outline_none",
          multiStyles.search_input,
          {
            cursor_text: !disabled,
            cursor_not_allowed: !!disabled,
          },
        )}
        ref={inputRef}
      />
    ) : null;
  return (
    // eslint-disable-next-line jsx-a11y/click-events-have-key-events, jsx-a11y/no-static-element-interactions
    <div
      className={sprout.classNames(
        "border_box",
        "box_shadow_transparent",
        "flex_noreset",
        "flex_row",
        "items_center",
        "justify_stretch",
        "w_full",
        "relative",
        "border_moderate",
        "radius_pill",
        "bg_default",
        "overflow_hidden",
        multiStyles.input,
        {
          [multiStyles.input_error]: !!hasError,
          [multiStyles.input_readonly]: !!readOnly,
          [multiStyles.input_disabled]: !!disabled,
        },
      )}
      data-testid={dataTestId}
      ref={ref}
      onClick={() => {
        if (inputRef.current) {
          inputRef.current.focus();
        }
      }}
    >
      <button
        type="button"
        className={sprout.classNames(
          "absolute",
          "bg_transparent",
          "outline_none",
          "border_none",
          "w_full",
          "h_full",
          "p_0",
          "m_0",
          "left_0",
          "top_0",
          {
            cursor_pointer: !disabled,
            cursor_not_allowed: !!disabled,
          },
        )}
        onClick={() => {
          if (disabled || readOnly) {
            return;
          }
          onDropDown?.();
          if (inputRef.current) {
            inputRef.current.focus();
          }
        }}
        aria-labelledby={ariaLabelledBy}
        aria-label={ariaLabel}
      />
      {renderValues(values || [], selectedProps, filterInput)}
      {!!onClear && !readOnly && !disabled && (
        <Tooltip title={labelClear || labelClearDefault}>
          <InputButton
            data-clear="true"
            id={`${id}-clear`}
            onClick={() => {
              controlled.onChange({
                target: {
                  value: "",
                },
              });
              onClear();
              if (inputRef.current) {
                inputRef.current.focus();
              }
            }}
            aria-label={labelClear || labelClearDefault}
            size="s"
          />
        </Tooltip>
      )}
      {!!hasError && (
        <ErrorOutlineIcon
          width={undefined}
          height={undefined}
          className={sprout.classNames(
            "self_center",
            "size_xl",
            "shrink_0",
            "mr_m",
            "text_danger",
          )}
        />
      )}
      <DropdownArrow
        className={sprout.classNames(
          "fill_default",
          "shrink_0",
          "self_center",
          {
            fill_disabled: !!disabled || !!readOnly,
          },
        )}
      />
    </div>
  );
}

ComboboxEditableBase.displayName = "ComboboxEditable";

