import { type Ref, forwardRef } from "react";

import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";

import style from "./SideNavigation.module.css";

export type SideNavProps = {
  variant?: "compact";
} & HTMLDivProps;

export const SideNav = forwardRef<HTMLDivElement, SideNavProps>(SideNavBase);

function SideNavBase(
  { variant, ...props }: SideNavProps,
  ref?: Ref<HTMLDivElement>,
) {
  return (
    <nav
      ref={ref}
      {...props}
      className={classNames("flex", "flex_col", "w_full", "gap_m", {
        [style.nav_compact]: variant === "compact",
      })}
    />
  );
}

SideNavBase.displayName = "SideNav.Nav";

import { type Ref, forwardRef } from "react";

import { classNames } from "../classNames";
import type { HTMLAnchorProps } from "../htmlTypes";
import {
  SideNavItemContent,
  type SideNavItemContentProps,
} from "./SideNavItemContent";

export type SideNavLinkItemProps = HTMLAnchorProps &
  Pick<
    SideNavItemContentProps,
    "variant" | "icon" | "iconSize" | "label" | "indentation" | "isExternal"
  >;

export const SideNavLinkItem = forwardRef<
  HTMLAnchorElement,
  SideNavLinkItemProps
>(SideNavLinkItemBase);

function SideNavLinkItemBase(
  {
    variant,
    icon,
    iconSize,
    label,
    indentation,
    isExternal,
    ...props
  }: SideNavLinkItemProps,
  ref?: Ref<HTMLAnchorElement>,
) {
  return (
    <a
      ref={ref}
      {...props}
      className={classNames("flex", "w_full", "no_underline")}
    >
      <SideNavItemContent
        variant={variant}
        icon={icon}
        iconSize={iconSize}
        label={label}
        indentation={indentation}
        isExternal={isExternal}
      />
    </a>
  );
}

SideNavLinkItemBase.displayName = "SideNav.Link";

import { type Ref, forwardRef } from "react";

import { useId } from "@qlik/sprout-css-hooks";

import { classNames } from "../classNames";
import type { HTMLSectionProps } from "../htmlTypes";

export type SideNavSectionProps = {
  label?: string;
  level?: "h2" | "h3" | "h4" | "h5" | "h6";
} & HTMLSectionProps;

export const SideNavSection = forwardRef<HTMLDivElement, SideNavSectionProps>(
  SideNavSectionBase,
);

function SideNavSectionBase(
  { label, level, children, ...props }: SideNavSectionProps,
  ref?: Ref<HTMLDivElement>,
) {
  const id = useId();
  const Title = level || "h2";
  return (
    <section
      ref={ref}
      className={classNames("flex", "flex_col")}
      {...props}
      aria-labelledby={label ? id : undefined}
    >
      {!!label && (
        <Title
          className={classNames(
            "flex",
            "py_l",
            "px_xl",
            "font_label_s_emphasized",
            "text_default",
          )}
          id={id}
        >
          {label}
        </Title>
      )}
      {children}
    </section>
  );
}

SideNavSectionBase.displayName = "SideNav.Section";

import { type CSSProperties, type Ref, forwardRef } from "react";

import { classNames } from "../classNames";
import type { HTMLSpanProps } from "../htmlTypes";

import styles from "./Skeleton.module.css";

type SkeletonVariant =
  | "text"
  | "circle"
  | "rectangle"
  | "Button"
  | "IconButton"
  | "Input"
  | "InputField";

export type SkeletonProps = HTMLSpanProps & {
  variant: SkeletonVariant;
  width?: CSSProperties["width"];
  height?: CSSProperties["height"];
};

export const Skeleton = forwardRef<HTMLSpanElement, SkeletonProps>(
  SkeletonBase,
);

function SkeletonBase(
  { width, height, variant = "text", ...props }: SkeletonProps,
  ref?: Ref<HTMLSpanElement>,
) {
  const style: CSSProperties = {
    inlineSize: width,
    blockSize: height,
  };
  if (variant === "InputField") {
    return (
      <div className={classNames("flex", "flex_col", "gap_m")}>
        <Skeleton variant="text" />
        <Skeleton variant="Input" />
      </div>
    );
  }
  return (
    <span
      className={styles.skeleton}
      {...props}
      data-variant={variant}
      ref={ref}
      aria-hidden
      style={style}
    />
  );
}

SkeletonBase.displayName = "Skeleton";

import { type Ref, forwardRef } from "react";

import { SliderIcon } from "@qlik/sprout-icons/react";

import type { InputProps } from "../Input";
import { classNames } from "../classNames";
import type { SliderMarkProps } from "./common.types";

import styles from "./Slider.module.css";

type SliderGripProps = {
  index?: number;
  currentPercent?: number;
  overlappingGrips?: boolean;
  orientation?: "horizontal" | "vertical";
  markValues?: Array<SliderMarkProps>;
  valueLabelFormat?: (value: number) => React.ReactNode;
} & InputProps;

export const SliderGrip = forwardRef<HTMLInputElement, SliderGripProps>(
  SliderGripBase,
);

function SliderGripBase(
  {
    id,
    currentPercent,
    value,
    index,
    step,
    overlappingGrips,
    orientation = "horizontal",
    markValues,
    valueLabelFormat,
    ...rest
  }: SliderGripProps,
  ref?: Ref<HTMLInputElement>,
) {
  const gripIndex = index ?? 0;
  const isVertical = orientation === "vertical";
  const dataListId = markValues ? `${id}-marks` : undefined;

  return (
    <div
      role="presentation"
      data-orientation={orientation}
      className={classNames(
        "absolute",
        "radius_subtle",
        "border_box",
        "border_default",
        "flex",
        "items_center",
        "justify_center",
        "box_shadow_default",
        styles.slider_grip,
      )}
      style={
        isVertical
          ? { insetBlockEnd: `${currentPercent}%` }
          : { insetInlineStart: `${currentPercent}%` }
      }
    >
      <output
        aria-live="off"
        htmlFor={id}
        data-orientation={orientation}
        className={classNames(
          "text_default",
          "font_label_s_emphasized",
          "absolute",
          "pointer_events_none",
          styles.slider_grip_value,
          {
            hidden: !!overlappingGrips && gripIndex === 1,
            text_disabled: !!rest.disabled,
          },
        )}
      >
        {valueLabelFormat ? valueLabelFormat(Number(value)) : value}
      </output>

      <SliderIcon />
      <input
        {...rest}
        aria-valuemin={Number(rest.min)}
        aria-valuemax={Number(rest.max)}
        aria-valuenow={Number(value)}
        aria-orientation={orientation}
        id={id}
        tabIndex={0}
        value={value}
        step={step}
        list={dataListId}
        className={classNames(
          "sr_only",
          "w_full",
          "h_full",
          styles.slider_input,
        )}
        type="range"
        ref={ref}
      />
      {Array.isArray(markValues) && dataListId !== undefined ? (
        <datalist id={dataListId}>
          {markValues.map((mark) => (
            <option
              key={mark.value}
              value={mark.value}
              aria-label={String(mark.value)}
            />
          ))}
        </datalist>
      ) : null}
    </div>
  );
}

SliderGripBase.displayName = "Slider.Grip";

import { classNames } from "../classNames";

import styles from "./Slider.module.css";

type SliderIndicatorProps = {
  start?: number;
  width?: number;
  disabled?: boolean;
  orientation?: "horizontal" | "vertical";
};

export function SliderIndicator({
  disabled,
  start,
  width,
  orientation = "horizontal",
}: SliderIndicatorProps) {
  const isVertical = orientation === "vertical";

  return (
    <div
      data-disabled={disabled}
      className={classNames(
        "radius_round",
        "border_box",
        "absolute",
        styles.slider_indicator,
        {
          h_full: !isVertical,
          w_full: isVertical,
        },
      )}
      role="presentation"
      style={
        isVertical
          ? { insetBlockEnd: `${start}%`, blockSize: `${width}%` }
          : { insetInlineStart: `${start}%`, inlineSize: `${width}%` }
      }
    />
  );
}

SliderIndicator.displayName = "Slider.Indicator";

import { classNames } from "../classNames";
import type {
  CommonSliderProps,
  PickedFieldProps,
  SliderMarkProps,
} from "./common.types";

import styles from "./Slider.module.css";

function TextAndMark({
  mark,
  disabled,
  orientation = "horizontal",
  showLabel = true,
  valueLabelFormat,
  verticalMarkLabels,
}: {
  mark: SliderMarkProps;
  disabled?: SliderIntervalsProps["disabled"];
  orientation?: CommonSliderProps["orientation"];
  showLabel?: boolean;
  valueLabelFormat?: (value: number) => React.ReactNode;
  verticalMarkLabels?: boolean;
}) {
  return (
    <>
      <div
        className={classNames("flex", "items_center", {
          px_m: orientation === "horizontal",
          px_xs: orientation === "vertical",
        })}
      >
        <span
          className={classNames(styles.slider_interval_mark)}
          data-orientation={orientation}
        />
      </div>
      {showLabel ? (
        <span
          className={
            classNames("font_label_s", "text_default", {
              text_disabled: !!disabled,
            }) +
            (verticalMarkLabels && orientation === "horizontal"
              ? ` ${styles.slider_interval_label_vertical}`
              : "")
          }
        >
          {mark.label ??
            (valueLabelFormat ? valueLabelFormat(mark.value) : mark.value)}
        </span>
      ) : null}
    </>
  );
}

type SliderIntervalsProps = Pick<
  CommonSliderProps,
  "max" | "min" | "orientation"
> &
  Pick<PickedFieldProps, "disabled"> & {
    markValues?: Array<SliderMarkProps>;
    markLabels?: boolean;
    verticalMarkLabels?: boolean;
    valueLabelFormat?: (value: number) => React.ReactNode;
  };

export function SliderIntervals({
  disabled,
  min,
  max,
  orientation = "horizontal",
  markValues,
  markLabels,
  verticalMarkLabels,
  valueLabelFormat,
}: SliderIntervalsProps) {
  if (min === undefined || max === undefined) {
    return null;
  }

  const isVertical = orientation === "vertical";
  const range = max - min;

  // Always show min and max, plus any mark values if provided
  const allValues = Array.isArray(markValues)
    ? markValues
    : [{ value: min }, { value: max }];

  return (
    <div
      className={classNames("relative", styles.slider_intervals_container, {
        w_full: !isVertical,
      })}
      data-orientation={orientation}
    >
      {allValues.map((mark) => {
        const normalizedPercent = ((mark.value - min) / range) * 100;
        const gripHalfWidth = "calc(var(--sprout-common-sizing-xl) / 2)";
        const adjustedPosition = `calc(${normalizedPercent}% * (100% - var(--sprout-common-sizing-xl)) / 100% + ${gripHalfWidth})`;

        return (
          <div
            key={mark.value}
            className={classNames("absolute", "flex", "items_center", {
              gap_m: !isVertical,
              gap_xs: isVertical,
              flex_col: !isVertical,
              flex_row: isVertical,
            })}
            style={
              isVertical
                ? {
                    insetBlockEnd: adjustedPosition,
                    transform: "translateY(50%)",
                  }
                : { left: adjustedPosition, transform: "translateX(-50%)" }
            }
          >
            <TextAndMark
              mark={mark}
              disabled={disabled}
              orientation={orientation}
              valueLabelFormat={valueLabelFormat}
              verticalMarkLabels={verticalMarkLabels}
              showLabel={
                mark.value === min ||
                mark.value === max ||
                markLabels === true ||
                ("label" in mark && mark.label !== undefined)
              }
            />
          </div>
        );
      })}
    </div>
  );
}

SliderIntervals.displayName = "Slider.Intervals";

import type { PropsWithChildren, Ref } from "react";
import { forwardRef } from "react";

import { classNames } from "../classNames";
import type { CommonSliderProps } from "./common.types";

import styles from "./Slider.module.css";

type SliderTrackProps = PropsWithChildren<{
  orientation?: CommonSliderProps["orientation"];
}>;

export const SliderTrack = forwardRef<HTMLDivElement, SliderTrackProps>(
  SliderTrackBase,
);

function SliderTrackBase(
  { children, orientation = "horizontal" }: SliderTrackProps,
  ref?: Ref<HTMLDivElement>,
) {
  return (
    <div
      className={classNames("cursor_pointer", styles.slider_track_wrapper, {
        w_full: orientation === "horizontal",
      })}
      data-orientation={orientation}
      role="presentation"
      ref={ref}
    >
      <div className={classNames("p_m")} role="presentation">
        <div
          className={classNames(
            "relative",
            "radius_round",
            "border_box",
            styles.slider_track,
          )}
          data-orientation={orientation}
        >
          {children}
        </div>
      </div>
    </div>
  );
}

SliderTrackBase.displayName = "Slider.Track";

import { type ReactNode, type Ref, forwardRef } from "react";

import { SEVERITY_ICONS } from "../Icons/SeverityIcon";
import { ProgressCircular } from "../Progress";
import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";
import { StepDivider } from "./StepDivider";
import { StepLine } from "./StepLine";

import styles from "./Stepper.module.css";

export type StepProps = {
  /** Define the content displayed */
  text: string;
  /** Define the visual step status indicator */
  semantic?: "completed" | "active" | "inactive" | "error";
  state?: "default" | "progress";
  /** @warning this property is controlled by the StepperContainer */
  number?: string;
  /** @warning this property is controlled by the StepperContainer */
  children?: ReactNode;
  isLast?: boolean;
} & HTMLDivProps;

export const Step = forwardRef<HTMLLIElement, StepProps>(StepBase);
Step.displayName = "Stepper.Step";

function StepBase(
  { number, semantic, text, children, state, isLast, ...rest }: StepProps,
  ref?: Ref<HTMLLIElement>,
) {
  const safeSemantic = semantic || "inactive";
  const safeState = state || "default";
  return (
    <li aria-current={safeSemantic === "active" ? "step" : false} ref={ref}>
      <div
        className={styles.step}
        data-semantic={semantic}
        {...rest}
        role={rest.onClick ? "button" : undefined}
        tabIndex={rest.tabIndex || rest.onClick ? 0 : undefined}
      >
        {safeState === "progress" && (
          <span
            className={classNames(
              "flex",
              "border_box",
              "flex_col",
              "items_center",
              "size_xxl",
              "justify_center",
            )}
          >
            <ProgressCircular />
          </span>
        )}
        {safeState === "default" && (
          <span
            className={classNames(
              "inline_flex",
              "border_box",
              "size_xxl",
              styles.indicator,
            )}
          >
            {(safeSemantic === "inactive" || safeSemantic === "active") && (
              <span
                className={classNames(
                  "inline_flex",
                  "radius_round",
                  "border_box",
                  "size_xxl",
                  "items_center",
                  "justify_center",
                  "font_label_s_emphasized",
                )}
              >
                {number}
              </span>
            )}
            {safeSemantic === "completed" && SEVERITY_ICONS["success"]}
            {safeSemantic === "error" && SEVERITY_ICONS["error"]}
          </span>
        )}
        <span className={styles.text}>{text}</span>
      </div>
      {!isLast && !children ? <StepLine /> : null}
      {children ? <StepDivider>{children}</StepDivider> : null}
    </li>
  );
}

