export function TableHeaderContent({
  header,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  variant,
  selection,
  selected,
  sortOrder,
  statusIcon,
  onSelectHeader,
  contextualMenu,
  additionalInfo,
  children,
  lines,
  ellipsis,
  ...props
}: TableHeaderProps & {
  /**
   * @deprecated it now reads it from the parent Table
   */
