TableHeaderContent.displayName = "Table.HeaderContent";

export const TableHeader = forwardRef<
  HTMLDivElement,
  TableHeaderProps & {
    /**
     * @deprecated it now reads it from the parent Table
     */
    variant?: "list" | "data";
  }
>(TableHeaderBase);

function TableHeaderBase(
  {
    variant,
    header,
    selection,
    selected,
    sortOrder,
    statusIcon,
    onSortChange,
    contextualMenu,
    onSelectHeader,
    onResize,
    resetSize,
    additionalInfo,
    children,
    width,
    ellipsis,
    lines,
    ...props
  }: TableHeaderProps & {
    /**
     * @deprecated it now reads it from the parent Table
     */
    variant?: "list" | "data";
  },
  ref?: Ref<HTMLDivElement>,
) {
  let style = {};
  if (width) {
    style = {
      flex: `1 1 ${getWidth(width)}`,
    };
  }

  let ariaSortOrder: "none" | "ascending" | "descending" | "other" | undefined;
  if (sortOrder === "asc") {
    ariaSortOrder = "ascending";
  } else if (sortOrder === "desc") {
    ariaSortOrder = "descending";
  }

  return (
    <div
      ref={ref}
      role="columnheader"
      data-variant={variant}
      {...props}
      className={classNames(
        "inline_flex",
        "cursor_pointer",
        "border_box",
        "flex_wrap",
        "outline_none",
        "relative",
        className.th,
      )}
      aria-sort={ariaSortOrder}
      tabIndex={0}
      onClick={() => {
        onSortChange?.(sortOrder === "asc" ? "desc" : "asc");
      }}
      onKeyDown={(e) => {
        if (e.key === "Enter") {
          e.stopPropagation();
          onSortChange?.(sortOrder === "asc" ? "desc" : "asc");
        }
      }}
      style={style}
    >
      <TableHeaderContent
        header={header}
        selection={selection}
        selected={selected}
        statusIcon={statusIcon}
        sortOrder={sortOrder}
        onSelectHeader={onSelectHeader}
        additionalInfo={additionalInfo}
        contextualMenu={contextualMenu}
        lines={lines}
        ellipsis={ellipsis}
        variant={variant}
      >
        {children}
      </TableHeaderContent>
      {!!onResize && !!resetSize && (
        <ResizingIndicator onResize={onResize} resetSize={resetSize} />
      )}
    </div>
  );
}
