import { type Ref, forwardRef, useMemo, useState } from "react";

import { classNames } from "../classNames";
import type { HTMLDivProps } from "../htmlTypes";
import { TableContext, type TableContextType } from "./context";
import { getWidth } from "./getWidth";

import className from "./Table.module.css";

export type TableProps = HTMLDivProps & {
  variant: "list" | "data";
  width?: number;
  height?: number;
  contentHeight?: number;
  overflowY?: TableContextType["overflowY"];
};

const containerFlex = classNames(
  "flex_noreset",
  "flex_row",
  "border_box",
  "size_full",
  "flex_nowrap",
);

export const Table = forwardRef<HTMLDivElement, TableProps>(TableBase);

function TableBase(
  {
    variant,
    children,
    width,
    height,
    contentHeight,
    overflowY,
    ...props
  }: TableProps,
  ref?: Ref<HTMLDivElement>,
) {
  const [scrollBarWidth, setScrollBarWidth] = useState(0);
  const contextValue = useMemo(() => {
    return {
      overflowY: overflowY ?? "default",
      containerHeight: height,
      contentHeight,
      scrollBarWidth,
      setScrollBarWidth,
    };
  }, [overflowY, height, contentHeight, scrollBarWidth, setScrollBarWidth]);

  return (
    <TableContext.Provider value={contextValue}>
      <div
        className={classNames(containerFlex, {
          overflow_y_auto: overflowY === "default",
        })}
        style={{ blockSize: height ? `${height}px` : undefined }}
      >
        <div
          {...props}
          role="grid"
          data-variant={variant}
          className={classNames(
            "flex_noreset",
            "flex_nowrap",
            "border_box",
            "flex_col",
            "overflow_x_auto",
            "size_full",
            "radius_subtle",
            "shrink_0",
            className.table,
          )}
          ref={ref}
          style={{
            flex: `1 1 ${getWidth(width)}`,
            blockSize: height ? `${height}px` : undefined,
          }}
        >
          {children}
        </div>
      </div>
    </TableContext.Provider>
  );
}

TableBase.displayName = "Table.Table";

import { type ReactNode, useContext } from "react";

import { Tooltip } from "../Tooltip";
import { classNames } from "../classNames";
import type { DataAttributes, HTMLButtonProps } from "../htmlTypes";
import { TabsInternalContext } from "./TabsContext";

import style from "./Tabs.module.css";

export type TabProps = Omit<
  HTMLButtonProps,
  "onClick" | "type" | "children"
> & {
  ["aria-controls"]: string;
  title: string;
  badge?: ReactNode;
  disabled?: boolean;
  icon?: ReactNode;
  useIconButton?: boolean;
} & DataAttributes;

export function Tab({ icon, badge, title, useIconButton, ...props }: TabProps) {
  const context = useContext(TabsInternalContext);
  const isSmall = context?.size === "s";
  const a11y: HTMLButtonProps = {
    role: "tab",
    "aria-selected": props["aria-controls"] === context?.value,
    tabIndex: props["aria-controls"] === context?.value ? 0 : -1,
  };
  if (useIconButton) {
    a11y["aria-label"] = title;
  }
  let content = (
    <button
      {...props}
      {...a11y}
      className={classNames(
        "flex",
        "flex_row",
        "border_box",
        "py_0",
        "justify_center",
        "items_center",
        style.tab,
        {
          [style.tab_small]: isSmall,
          px_l: !isSmall,
          px_0: isSmall,
        },
      )}
      onClick={(e) => context?.onChange(e, props["aria-controls"])}
      type="button"
    >
      <div
        className={classNames(
          "flex",
          "flex_row",
          "border_box",
          "content_center",
          "items_center",
          "self_stretch",
          "gap_m",
          "justify_center",
          style.inner,
        )}
      >
        {icon}
        {!useIconButton && (
          <span
            className={classNames(style.tab__label, {
              font_label_s_emphasized: !isSmall,
              font_label_xs_emphasized: isSmall,
            })}
          >
            {title}
          </span>
        )}
        {badge}
      </div>
    </button>
  );
  if (useIconButton) {
    content = <Tooltip title={title}>{content}</Tooltip>;
  }
  return content;
}
Tab.displayName = "Tabs.Tab";

import { useContext, useEffect, useRef } from "react";

import { classNames } from "../classNames";
import { useListGesture } from "../hooks/useListGesture";
import { TabsInternalContext } from "./TabsContext";
import { Tracker } from "./Tracker";

import style from "./Tabs.module.css";

export type TabListProps = {
  showTrack?: boolean;
  children: React.ReactNode;
};

function getAllItems(ref: HTMLElement): NodeList {
  return ref.querySelectorAll(`[role="tab"]:not(:disabled)`);
}
function getSelectedItems(ref: HTMLElement): NodeList {
  return ref.querySelectorAll(
    `[role="tab"][aria-selected="true"]:not(:disabled)`,
  );
}

export function TabList({ showTrack, children }: TabListProps) {
  const ref = useRef<HTMLDivElement>(null);
  const context = useContext(TabsInternalContext);
  const isSmall = context?.size === "s";
  const gesture = useListGesture({
    getAllItems,
    getSelectedItems,
    orientation: "horizontal",
  });
  useEffect(() => {
    if (ref.current) {
      gesture.setElement(ref.current);
    }

    return () => {
      gesture.setElement();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div
      className={classNames("flex", "flex_row", style.tablist, {
        [style.show_track]: !!showTrack,
        gap_l: isSmall,
      })}
      role="tablist"
      ref={ref}
    >
      {children}
      <Tracker />
    </div>
  );
}
TabList.displayName = "Tabs.List";

