/* eslint-disable consistent-return */
import { memo, useContext, useEffect, useRef, useState } from "react";

import { TabsInternalContext } from "./TabsContext";

import style from "./Tabs.module.css";

export const Tracker = memo(TrackerBase);

function TrackerBase() {
  const context = useContext(TabsInternalContext);
  const ref = useRef<HTMLDivElement>(null);
  const [start, setStart] = useState<number>(-10000);
  const [width, setWidth] = useState<number>(100);

  useEffect(() => {
    let navResizeObserver: ResizeObserver | undefined;
    let tabResizeObserver: ResizeObserver | undefined;
    let tab: Element | null;
    let parentEl: Element | null;

    const updateTracker = () => {
      if (!parentEl || !tab?.parentElement) return;

      const navRect = parentEl.getBoundingClientRect();
      const tabParentRect = tab.parentElement.getBoundingClientRect();

      const isRTL = getComputedStyle(parentEl).direction === "rtl";
      const newStart = isRTL
        ? Math.floor(navRect.right - tabParentRect.right)
        : Math.floor(tabParentRect.left - navRect.left);

      setStart((prev) => (prev !== newStart ? newStart : prev));

      const newWidth = Math.floor(tabParentRect.width) + 1;
      setWidth((prev) => (prev !== newWidth ? newWidth : prev));
    };

    const returnFn = () => {
      navResizeObserver?.disconnect();
      tabResizeObserver?.disconnect();
      navResizeObserver = undefined;
      tabResizeObserver = undefined;
    };
    if (context?.value && ref.current) {
      parentEl = ref.current.parentElement;
      if (!parentEl) return undefined;
      tab = parentEl.querySelector(`[aria-controls="${context.value}"] > div`);
      if (!tab) return undefined;
      navResizeObserver = new ResizeObserver(updateTracker);
      tabResizeObserver = new ResizeObserver(updateTracker);
      navResizeObserver.observe(parentEl);
      tabResizeObserver.observe(tab);
      updateTracker();
    }

    return returnFn;
  }, [context]);
  return (
    <div
      className={style.tracker}
      data-testid="tab-tracker"
      style={{ insetInlineStart: start, width }}
      ref={ref}
    />
  );
}
TrackerBase.displayName = "Tracker";

/* eslint-disable @eslint-react/hooks-extra/no-direct-set-state-in-use-effect */
import {
  type ReactNode,
  useEffect,
  useLayoutEffect,
  useRef,
  useState,
} from "react";

import { Tooltip } from "../Tooltip";
import { classNames } from "../classNames";
import { onVisible } from "../hooks/useHasOverflow";

export type TextOverflowProps = {
  /** @deprecated please use text instead */
  children?: ReactNode;
  className: string;
  variant: "tooltip" | "ellipsis" | "wrap";
  text?: string;
  maxLength?: number;
};

function TextOverflowTooltip({
  children,
  className,
  text,
  maxLength,
}: Pick<TextOverflowProps, "children" | "text" | "maxLength" | "className">) {
  const textRef = useRef<HTMLSpanElement | null>(null);
  const [overflow, setOverflow] = useState(
    text && maxLength && text.length > maxLength,
  );

  useLayoutEffect(() => {
    if (textRef.current && !maxLength) {
      onVisible(textRef.current, () => {
        const width = textRef.current?.clientWidth;
        const scrollWidth = textRef.current?.scrollWidth;
        if (width && scrollWidth && width < scrollWidth) {
          setOverflow(true);
        }
      });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    const textExceedsMaxLength = text && maxLength && text.length > maxLength;
    if (!overflow && textExceedsMaxLength) {
      setOverflow(true);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [text, maxLength]);

  let safeText = text;
  if (text && maxLength && text.length > maxLength) {
    safeText = `${text.slice(0, maxLength)}`;
  }

  let content = (
    <span className={className} ref={textRef}>
      {safeText || children}
    </span>
  );

  if (overflow) {
    content = (
      <Tooltip title={text || children}>
        <span className={className} aria-label={text}>
          {safeText || children}
        </span>
      </Tooltip>
    );
  }
  return content;
}

/**
 * Manage text overflow. Enforce overflow: hidden
 * @param mode - "tooltip" | "ellipsis" | "wrap"
 */
export function TextOverflow({
  children,
  text,
  maxLength,
  className,
  variant,
}: TextOverflowProps) {
  const textClasses = classNames("overflow_hidden", {
    [className]: !!className,
    text_nowrap: variant === "tooltip",
    ellipsis: variant === "ellipsis" || variant === "tooltip",
    text_wrap: variant === "wrap",
    break_words: variant === "wrap",
  });
  if (variant === "tooltip") {
    return (
      <TextOverflowTooltip
        text={text}
        maxLength={maxLength}
        className={textClasses}
      >
        {children}
      </TextOverflowTooltip>
    );
  }
  return <span className={textClasses}>{children}</span>;
}

import { expect, test } from "@playwright/experimental-ct-react";

import { ThemeProvider } from "./ThemeProvider";
import {
  ThemeExposeIsTouch,
  ThemeProviderCascade,
  ThemeProviderGlobal,
  ThemeProviderWithManualSetup,
} from "./ThemeProviderTestCase";

test(`should ThemeProvider read global`, async ({ mount }) => {
  const component = await mount(<ThemeProviderGlobal />);
  await expect(component).toHaveAttribute("data-qlik-appearance", "qlik-light");
  await expect(component).toHaveAttribute("data-qlik-density", "comfortable");
});

test(`should ThemeProvider cascade`, async ({ mount, page }) => {
  await mount(<ThemeProviderCascade />);
  const root = page.getByTestId("root");
  const nested = page.getByTestId("nested");
  // test casing. No uppercase letters should be present
  expect(
    await root.evaluate((el) =>
      el.getAttributeNames().some((n) => /[A-Z]/.test(n)),
    ),
  ).toBe(false);
  // test global
  await expect(root).toHaveAttribute("data-qlik-appearance", "qlik-light");
  await expect(nested).toHaveAttribute("data-qlik-appearance", "qlik-light");
  // test context
  await expect(root).toHaveAttribute("data-qlik-density", "dense");
  await expect(nested).toHaveAttribute("data-qlik-density", "dense");
  // test props
  await expect(root).toHaveAttribute("data-qlik-roundness", "soft");
  await expect(nested).toHaveAttribute("data-qlik-roundness", "rounded");
  // test DEFAULT_THEME
  await expect(root).toHaveAttribute("data-qlik-sizing", "mid-sized");
  await expect(nested).toHaveAttribute("data-qlik-sizing", "mid-sized");
});

test("should ThemeProvider work with manual setup", async ({ mount, page }) => {
  await mount(<ThemeProviderWithManualSetup />);
  const deep = await page.getByTestId("deep");
  await expect(deep).toHaveAttribute("data-qlik-appearance", "qlik-dark");
  await expect(deep).toHaveAttribute("data-qlik-density", "dense");
  await expect(deep).toHaveAttribute("data-qlik-sizing", "foo");
  await expect(deep).toHaveAttribute("data-qlik-typography", "source-sans");
});

test(`should ThemeProvider expose isTouch in context`, async ({
  mount,
  page,
}) => {
  const comp = await mount(<ThemeExposeIsTouch isTouch />);
  const div = await page.getByTestId("isTouch");
  await expect(div).toHaveText("true");
  await comp.update(<ThemeExposeIsTouch isTouch={false} />);
  await expect(div).toHaveText("false");
});

