import { useContext, useEffect } from "react";

import { applyTheme } from "@qlik/sprout-theme-provider";

import { ThemeProvider, useTheme } from "./ThemeProvider";

function useRemoveBodyDataset() {
  useEffect(() => {
    // playwright body setup of default theme we need to drop
    // @ts-ignore
    delete document.body.dataset.theme;
    // @ts-ignore
    delete document.body.dataset.appearance;
    // @ts-ignore
    delete document.body.dataset.density;
    // @ts-ignore
    delete document.body.dataset.roundness;
    // @ts-ignore
    delete document.body.dataset.sizing;
    // @ts-ignore
    delete document.body.dataset.typography;
    return () => {
      applyTheme(document.body);
    };
  }, []);
}

export function ThemeProviderGlobal() {
  useRemoveBodyDataset();
  return (
    <ThemeProvider asDiv id="component-testing">
      up
    </ThemeProvider>
  );
}

export function ThemeProviderCascade() {
  useRemoveBodyDataset();

  return (
    <ThemeProvider asDiv data-testid="root" data-qlik-density="dense">
      <ThemeProvider asDiv data-testid="nested" data-qlik-roundness="rounded">
        up
      </ThemeProvider>
    </ThemeProvider>
  );
}

export function ThemeProviderWithManualSetup() {
  useRemoveBodyDataset();

  return (
    <div data-qlik-sizing="foo" data-qlik-typography="source-sans">
      <div data-qlik-appearance="qlik-dark">
        <div data-qlik-density="dense">
          <ThemeProvider asDiv data-testid="deep">
            up
          </ThemeProvider>
        </div>
      </div>
    </div>
  );
}

function ReadIsTouch() {
  const theme = useTheme();
  return <div data-testid="isTouch">{theme.isTouch ? "true" : "false"}</div>;
}

export function ThemeExposeIsTouch({ isTouch }: { isTouch?: boolean }) {
  return (
    <ThemeProvider isTouch={isTouch}>
      <ReadIsTouch />
    </ThemeProvider>
  );
}

import {
  type ReactNode,
  type Ref,
  forwardRef,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";

import { useId } from "@qlik/sprout-css-hooks";
import CrossOutline from "@qlik/sprout-icons/react/CrossOutline";

import type { AlertInlineSeverity } from "../AlertInline";
import { Button, type ButtonProps, IconButton } from "../Button";
import { Dialog } from "../Dialog";
import { SEVERITY_ICONS } from "../Icons/SeverityIcon";
import { mergeRefs } from "../Utils/mergeRef";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { HTMLDivProps } from "../htmlTypes";

import style from "./Toast.module.css";

export type ToastProps = {
  title?: string;
  action?: ButtonProps;
  severity?: AlertInlineSeverity;
  message: ReactNode;
  "data-testid"?: string;
  onClose?: (e?: KeyboardEvent) => void;
} & HTMLDivProps;

const TOAST_CLASS: Record<AlertInlineSeverity, string> = {
  info: style.toast_info,
  warning: style.toast_warning,
  error: style.toast_error,
  success: style.toast_success,
};
export const Toast = forwardRef<HTMLDivElement, ToastProps>(ToastBase);

function ToastBase(
  {
    title,
    message,
    action,
    severity,
    onClose,
    "data-testid": dataTestId,
    ...props
  }: ToastProps,
  ref?: Ref<HTMLDivElement>,
) {
  const [show, setShow] = useState(false);
  const dialogRef = useRef<HTMLDivElement | null>(null);
  const [mount, setMount] = useState(true);
  const titleId = useId();
  const descriptionId = useId();
  const t = useI18n();
  const dismissLabel = t("toast.dismiss");
  const onCloseToast = useMemo(() => {
    return (e?: KeyboardEvent) => {
      setShow(false);
      setTimeout(() => {
        onClose?.(e);
        setMount(false);
      }, 400);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const mergeRef = mergeRefs([ref, dialogRef]);

  useEffect(() => {
    const clear = setTimeout(() => {
      setShow(true);
    }, 0);
    const closeListener = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        onCloseToast(e);
      }
    };
    document.addEventListener("keydown", closeListener);
    return () => {
      document.removeEventListener("keydown", closeListener);
      clearTimeout(clear);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const role: HTMLDivProps = {
    role: "alert",
    "aria-labelledby": descriptionId,
  };

  if (title) {
    role["aria-labelledby"] = titleId;
    role["aria-describedby"] = descriptionId;
  }

  if (!mount) {
    return null;
  }

  const actionBtn = action && (
    <div className={classNames("self_start")}>
      <Button
        variant="quiet"
        size="small"
        data-testid={dataTestId ? `${dataTestId}-action` : undefined}
        {...action}
        onClick={(e) => {
          action.onClick?.(e);
          onCloseToast();
        }}
      />
    </div>
  );

  return (
    <Dialog
      ref={mergeRef}
      data-testid={dataTestId}
      visible
      {...role}
      {...props}
      className={classNames(
        "inline_flex",
        "border_box",
        "items_start",
        "border_moderate",
        "radius_subtle",
        "bg_default",
        "text_default",
        "box_shadow_moderate",
        "relative",
        "p_m",
        style.toast,
        {
          [TOAST_CLASS[severity || "info"]]: !!severity,
          [style.show]: show,
          pl_xl: !severity,
          pr_xl: !onClose && !action,
        },
      )}
    >
      <div
        className={classNames(
          "flex",
          "w_full",
          "flex_row",
          "gap_s",
          "items_center",
          "relative",
        )}
      >
        {!!severity && (
          <div
            className={classNames(
              "flex",
              "items_center",
              "justify_center",
              "p_s",
              style.severity_icon,
              {
                as_start: !!title || (!!message && !title),
              },
            )}
          >
            {SEVERITY_ICONS[severity]}
          </div>
        )}
        <div data-type="content" className={classNames("overflow_hidden")}>
          {!!title && (
            <div
              className={classNames(
                "flex",
                "flex_row",
                "gap_s",
                "items_center",
              )}
            >
              <strong
                className={classNames(
                  "w_full",
                  "font_label_xs_emphasized",
                  "ellipsis",
                  "my_s",
                )}
                id={titleId}
                data-testid={dataTestId ? `${dataTestId}-title` : undefined}
              >
                {title}
              </strong>
              {actionBtn}
            </div>
          )}
          {typeof message === "string" ? (
            <div
              className={classNames(
                "flex",
                "flex_row",
                "items_center",
                "gap_s",
              )}
            >
              <p
                className={classNames(
                  "font_label_xs",
                  "m_0",
                  "overflow_hidden",
                  style.max_rows,
                  {
                    mb_s: !!title,
                    mr_s: !!onClose,
                  },
                )}
                id={descriptionId}
                data-testid={dataTestId ? `${dataTestId}-message` : undefined}
              >
                {message}
              </p>
              {!title && actionBtn}
            </div>
          ) : (
            <div
              className={classNames(
                "font_label_xs",
                "m_0",
                "overflow_y_auto",
                "overflow_x_hidden",
                style.max_height,
                {
                  mb_s: !!title,
                  mr_s: !!onClose,
                },
              )}
              id={descriptionId}
              data-testid={dataTestId ? `${dataTestId}-message` : undefined}
            >
              {message}
            </div>
          )}
        </div>

        {typeof message !== "string" && !title && actionBtn}

        {!!onClose && (
          <div className={classNames("shrink_0", "grow_0", "self_start")}>
            <IconButton
              size="small"
              variant="quiet"
              onClick={() => {
                onCloseToast();
              }}
              icon={<CrossOutline height={undefined} />}
              aria-label={dismissLabel}
              data-testid={dataTestId ? `${dataTestId}-close` : undefined}
            />
          </div>
        )}
      </div>
    </Dialog>
  );
}

ToastBase.displayName = "Toast.Content";

import { type ReactNode, type Ref, forwardRef, isValidElement } from "react";

import { useId } from "@qlik/sprout-css-hooks";
import DropdownArrow from "@qlik/sprout-icons/react/DropdownArrow";

import { Badge } from "../Badge";
import { Tooltip, type TooltipProps } from "../Tooltip";
import { classNames } from "../classNames";
import type { ToggleButtonProps } from "./ToggleButton";
import { useToggle } from "./hook";

import style from "../Button/Button.module.css";

export type ToggleIconButtonProps = ToggleButtonProps & {
  placement?: TooltipProps["placement"];
  icon: ReactNode;
};

export const ToggleIconButton = forwardRef(BaseToggleIconButton);

function BaseToggleIconButton(
  {
    label,
    badge,
    icon,
    placement,
    variant,
    size,
    onChange,
    toggled,
    asDropdown = false,
    defaultToggled,
    id,
    ...props
  }: ToggleIconButtonProps,
  ref?: Ref<HTMLButtonElement>,
) {
  const tooltipId = useId();
  const controlled = useToggle({
    onChange,
    toggled,
    defaultToggled,
  });

  let badgeContent = null;
  if (badge) {
    if (isValidElement(badge)) {
      badgeContent = badge;
    } else if (typeof badge === "object") {
      badgeContent = <Badge {...badge} />;
    }
  }
  return (
    <Tooltip id={tooltipId} title={label} placement={placement} ref={ref}>
      <button
        id={id}
        aria-describedby={tooltipId}
        type="button"
        aria-label={label}
        // label is only for a11y and not visible on the button
        className={classNames(style.button, style.toggle, style.nolabel)}
        data-variant={variant || "default"}
        data-size={size || "default"}
        aria-pressed={controlled.value || false}
        onClick={() => {
          controlled.onChange(!controlled.value);
        }}
        {...props}
      >
        <span className={classNames("gap_m", style.content)}>
          {icon ? <span data-type="icon">{icon}</span> : null}
          {badgeContent ? (
            <span className={style.badge}>{badgeContent}</span>
          ) : null}
          {asDropdown ? <DropdownArrow data-dropdown data-type="icon" /> : null}
        </span>
      </button>
    </Tooltip>
  );
}

BaseToggleIconButton.displayName = "ToggleIconButton";

