import { useEffect, useRef, useState } from "react";

import { Button } from "../Button";
import { classNames } from "../classNames";
import { Tooltip, type TooltipProps } from "./Tooltip";

const TITLE = [
  "This is a simple tooltip",
  "This is a very long tooltip that should trigger the overflow handling of the tooltip component in order to make sure that it is displayed correctly within the viewport",
];

export function TooltipSimpleTest() {
  return (
    <div
      className={classNames(
        "flex",
        "flex_col",
        "border_box",
        "p_s",
        "items_start",
        "justify_start",
        "border_default",
      )}
      style={{ inlineSize: "200px", blockSize: "200px" }}
    >
      <Tooltip placement="bottom" title={TITLE[0]}>
        <Button label="b" />
      </Tooltip>
    </div>
  );
}

export function TooltipOverflowTest({ title }: { title?: string }) {
  return (
    <div
      className={classNames(
        "flex",
        "flex_col",
        "border_box",
        "p_s",
        "items_center",
        "justify_center",
        "border_default",
      )}
      style={{
        inlineSize: "var(--sprout-container-sizing-xxs)",
        blockSize: "var(--sprout-container-sizing-xxs)",
      }}
    >
      <Tooltip title={title}>
        <Button label="button" />
      </Tooltip>
    </div>
  );
}

export function TooltipWithRefTest() {
  const ref = useRef<HTMLButtonElement>(null);
  useEffect(() => {
    if (ref.current) {
      ref.current.innerHTML = "Hello world";
    }
  }, []);
  return (
    <Tooltip title="tooltip" ref={ref}>
      <button type="button">Hover me</button>
    </Tooltip>
  );
}

export function ControlledClickTooltip() {
  const [open, setOpen] = useState(true);

  function handleClick() {
    setOpen(!open);
  }

  return (
    <div
      className={classNames(
        "flex",
        "justify_center",
        "items_center",
        "w_s",
        "mt_3xl",
      )}
    >
      <Tooltip title="This should be visible if open is true" open={open}>
        <button onClick={handleClick} type="button">
          Tooltip
        </button>
      </Tooltip>
    </div>
  );
}

export function TooltipTest(props: TooltipProps) {
  return (
    <div
      id="component-testing"
      style={{ inlineSize: 200, blockSize: 200 }}
      className={classNames("p_s", "items_start")}
    >
      <Tooltip {...props}>
        <Button label="button" />
      </Tooltip>
    </div>
  );
}

export function MultipleTooltipsTest() {
  return (
    <div
      id="component-testing"
      className={classNames(
        "flex",
        "flex_row",
        "border_box",
        "p_s",
        "items_start",
        "gap_m",
      )}
      style={{ inlineSize: "200px", blockSize: "200px" }}
    >
      <Tooltip title={TITLE[0]}>
        <Button label="button-1" />
      </Tooltip>

      <Tooltip title={TITLE[1]}>
        <Button label="button-2" />
      </Tooltip>
    </div>
  );
}

import type { ReactNode } from "react";

import FolderOpenOutline from "@qlik/sprout-icons/react/FolderOpenOutline";
import FolderOutline from "@qlik/sprout-icons/react/FolderOutline";

import { IconButton } from "../Button";
import { classNames } from "../classNames";
import { useI18n } from "../hooks/useI18n";
import type { HTMLDivProps } from "../htmlTypes";

export type TreeItemProps = {
  id: string;
  label: string;
  path?: Array<string>;
  isLeaf?: boolean;
  /**
   * @deprecated please use icon property instead
   */
  displayFolderIcon?: boolean;
  disabled?: boolean;
  nesting?: number;
  expanded?: boolean;
  onExpand?: (id: string, expanded: boolean) => void;
  icon?: ReactNode;
  children?: ReactNode;
} & HTMLDivProps;

function DropDown() {
  return (
    <svg viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
      <g id="icons/outline/dropdown_arrow_16">
        <path
          id="Union"
          fillRule="evenodd"
          clipRule="evenodd"
          d="M7.999 9.001L5.14149 6.14159C5.06337 6.06341 4.93664 6.06342 4.85853 6.14161L4.14126 6.8596C4.06323 6.93771 4.06325 7.06427 4.1413 7.14235L6.999 10.001L7.85772 10.8589C7.93578 10.9368 8.06225 10.9369 8.14035 10.8589L9 10.001L11.8586 7.14235C11.9367 7.06427 11.9368 6.93769 11.8587 6.85958L11.1414 6.14156C11.0633 6.06339 10.9366 6.06336 10.8585 6.14149L7.999 9.001Z"
          fill="currentColor"
        />
      </g>
    </svg>
  );
}

function DropRight() {
  return (
    <svg viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
      <g id="icons/outline/dropright_arrow_16">
        <path
          id="Union"
          fillRule="evenodd"
          clipRule="evenodd"
          d="M8.501 8.501L5.64159 11.3585C5.56341 11.4366 5.56342 11.5634 5.64161 11.6415L6.3596 12.3587C6.43771 12.4368 6.56427 12.4368 6.64235 12.3587L9.501 9.501L10.3589 8.64228C10.4368 8.56422 10.4369 8.43775 10.3589 8.35965L9.501 7.5L6.64235 4.64135C6.56427 4.56327 6.43769 4.56324 6.35958 4.64128L5.64156 5.35858C5.56339 5.43667 5.56336 5.56336 5.64149 5.64149L8.501 8.501Z"
          fill="currentColor"
        />
      </g>
    </svg>
  );
}

export function TreeItem({
  id,
  children,
  label,
  path,
  disabled,
  expanded,
  onExpand,
  nesting,
  icon,
  displayFolderIcon,
  isLeaf,
  ...props
}: TreeItemProps) {
  const t = useI18n();

  const safeExpanded = !!expanded;
  let safeNesting = nesting || 0;
  if (path) {
    safeNesting = path.length;
  }

  const onClick = () => {
    if (onExpand) {
      onExpand(id, !safeExpanded);
    }
  };

  let safeIcon = icon;
  if (displayFolderIcon) {
    safeIcon = safeExpanded ? <FolderOpenOutline /> : <FolderOutline />;
  }

  return (
    <div
      id={id}
      className={classNames(
        "relative",
        "flex",
        "flex_row",
        "items_center",
        "font_label_s",
        "py_s",
        "w_full",
        "overflow_hidden",
        {
          text_default: !disabled,
          text_disabled: !!disabled,
        },
      )}
      {...props}
    >
      <div
        className={classNames("pl_m", "shrink_0")}
        style={{
          inlineSize: `calc(${safeNesting} * var(--sprout-common-sizing-xxl))`,
        }}
      />

      <span
        className={classNames({
          invisible: !!isLeaf,
        })}
      >
        <IconButton
          variant="quiet"
          size="small"
          disabled={disabled}
          tabIndex={-1}
          icon={safeExpanded ? <DropDown /> : <DropRight />}
          onClick={onClick}
          aria-label={
            safeExpanded
              ? t("treeitem.label.close")
              : t("treeitem.label.expand")
          }
        />
      </span>

      <div
        data-layoutid="IconItem"
        className={classNames(
          "flex",
          "flex_row",
          "items_center",
          "gap_s",
          "w_full",
          "overflow_hidden",
        )}
      >
        {safeIcon ? (
          <span className={classNames("shrink_0", "flex", "items_center")}>
            {safeIcon}
          </span>
        ) : null}
        <div className={classNames("flex", "flex_row", "flex_nowrap", "w_max")}>
          {label}
        </div>
        {children}
      </div>
    </div>
  );
}

TreeItem.displayName = "Tree.TreeItem";

import { type CSSProperties, type Ref, forwardRef } from "react";

import { classNames } from "../classNames";
import type { HTMLButtonProps } from "../htmlTypes";

import style from "./Clickable.module.css";

export type ClickableProps = {
  className?: string;
  style?: CSSProperties;
} & HTMLButtonProps;

export const Clickable = forwardRef<HTMLButtonElement, ClickableProps>(
  ClickableBase,
);

function ClickableBase(
  { className, disabled, ...props }: ClickableProps,
  ref?: Ref<HTMLButtonElement>,
) {
  return (
    <button
      ref={ref}
      type="button"
      {...props}
      disabled={disabled}
      className={classNames(
        "p_0",
        "m_0",
        "border_box",
        "border_none",
        "bg_transparent",
        "outline_none",
        style.clickable,
        {
          [className || ""]: !!className,
          cursor_default: !!disabled,
          cursor_pointer: !disabled,
        },
      )}
    />
  );
}
ClickableBase.displayName = "Clickable";

import type { ReactNode } from "react";

import { InfoOutlineIcon } from "@qlik/sprout-icons/react";

import { Popover } from "../Popover";
