import type { CSSProperties, ReactNode } from "react";

import { InfoOutlineIcon } from "@qlik/sprout-icons/react";

import { Tooltip } from "../Tooltip";
import { Clickable } from "../_Clickable/Clickable";
import { classNames } from "../classNames";
import type { HTMLButtonProps } from "../htmlTypes";

export type InfoTooltipPrimitiveProps = {
  title: ReactNode;
  className?: string;
  style?: CSSProperties;
} & Omit<HTMLButtonProps, "title">;

export function InfoTooltipPrimitive({
  title,
  className,
  disabled,

  ...rest
}: InfoTooltipPrimitiveProps) {
  const button = (
    <Clickable
      {...rest}
      disabled={disabled}
      data-testid="info-tooltip-button"
      className={classNames("inline_flex", "size_l", {
        [className || ""]: !!className,
      })}
      type="button"
    >
      <InfoOutlineIcon
        width={undefined}
        height={undefined}
        className={classNames("shrink_0", "fill_current")}
      />
    </Clickable>
  );

  return disabled ? (
    button
  ) : (
    <Tooltip title={title} placement="top">
      {button}
    </Tooltip>
  );
}
InfoTooltipPrimitive.displayName = "InfoTooltipPrimitive";

/* eslint-disable @eslint-react/hooks-extra/no-direct-set-state-in-use-effect */
import { type RefObject, useEffect, useState } from "react";

import de from "../translations/de.json";
import dePlural from "../translations/de.plural.json";
import en from "../translations/en.json";
import enPlural from "../translations/en.plural.json";
import es from "../translations/es.json";
import esPlural from "../translations/es.plural.json";
import fr from "../translations/fr.json";
import frPlural from "../translations/fr.plural.json";
import it from "../translations/it.json";
import itPlural from "../translations/it.plural.json";
import ja from "../translations/ja.json";
import jaPlural from "../translations/ja.plural.json";
import ko from "../translations/ko.json";
import koPlural from "../translations/ko.plural.json";
import nl from "../translations/nl.json";
import nlPlural from "../translations/nl.plural.json";
import pl from "../translations/pl.json";
import plPlural from "../translations/pl.plural.json";
import pt from "../translations/pt.json";
import ptPlural from "../translations/pt.plural.json";
import ru from "../translations/ru.json";
import ruPlural from "../translations/ru.plural.json";
import sv from "../translations/sv.json";
import svPlural from "../translations/sv.plural.json";
import tr from "../translations/tr.json";
import trPlural from "../translations/tr.plural.json";
import zhCN from "../translations/zh-CN.json";
import zhCNPlural from "../translations/zh-CN.plural.json";
import zhTW from "../translations/zh-TW.json";
import zhTWPlural from "../translations/zh-TW.plural.json";

export type TranslationSlot = {
  value: string;
  comment: string;
};

export type TranslationPluralSlot = {
  value: {
    zero: string;
    one: string;
    other: string;
  };
  comment: string;
};

const cachePlurals: Record<
  HTMLHtmlElement["lang"],
  Record<string, TranslationPluralSlot>
> = {
  de: dePlural,
  en: enPlural,
  es: esPlural,
  fr: frPlural,
  it: itPlural,
  ja: jaPlural,
  ko: koPlural,
  nl: nlPlural,
  pl: plPlural,
  pt: ptPlural,
  ru: ruPlural,
  sv: svPlural,
  tr: trPlural,
  "zh-CN": zhCNPlural,
  "zh-TW": zhTWPlural,
};

const cache: Record<
  HTMLHtmlElement["lang"],
  Record<string, TranslationSlot>
> = {
  de,
  en,
  es,
  fr,
  it,
  ja,
  ko,
  nl,
  pl,
  pt,
  ru,
  sv,
  tr,
  "zh-CN": zhCN,
  "zh-TW": zhTW,
};

function sanitizeLang(lang?: string): HTMLHtmlElement["lang"] {
  if (!lang) {
    return "en";
  }
  if (lang === "zh-CN" || lang === "zh-TW") {
    return lang;
  }
  return lang.split("-")[0];
}

export function useI18n(ref?: RefObject<HTMLElement | null>) {
  let queryFn = (selector: string) => document.querySelector(selector);
  if (ref) {
    queryFn = (selector) => {
      if (ref.current) {
        return ref.current.lang
          ? ref.current
          : ref.current.closest(selector) || null;
      }
      return null;
    };
  }
  const [langState, setLangState] = useState("en");

  useEffect(() => {
    const withLangEl = queryFn("[lang]") as HTMLElement | null;
    const lang: HTMLHtmlElement["lang"] = sanitizeLang(
      withLangEl?.getAttribute("lang") || undefined,
    );
    if (langState !== lang) {
      setLangState(lang);
    }
    // observe changes to lang attribute
    const observer = new MutationObserver(() => {
      const newLang = sanitizeLang(
        withLangEl?.getAttribute("lang") || undefined,
      );
      if (langState !== newLang) {
        setLangState(newLang);
      }
    });
    if (withLangEl) {
      observer.observe(withLangEl, { attributes: true });
    }
    return () => {
      observer.disconnect();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const [translations, setTranslations] = useState<
    Record<string, TranslationSlot>
  >(cache[langState]);

  const [plurals, setPlurals] = useState<Record<string, TranslationPluralSlot>>(
    cachePlurals[langState],
  );

  useEffect(() => {
    if (langState && cache[langState] !== translations) {
      setTranslations(cache[langState]);
    }

    if (langState && cachePlurals[langState] !== plurals) {
      setPlurals(cachePlurals[langState]);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [langState]);

  return (key: string, plural?: number, opts?: Record<string, string>) => {
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
    if (plural !== undefined && plurals[key]) {
      const zero = plurals[key].value.zero || plurals[key].value.other;
      const one = plurals[key].value.one || plurals[key].value.other;
      const other = plurals[key].value.other;

      if (plural === 0 && zero) {
        return interpolate(zero, {
          count: `${plural}`,
          ...(opts || {}),
        });
      }
      if (plural === 1 && one) {
        return interpolate(one, { count: `${plural}`, ...(opts || {}) });
      }
      if (plural > 1) {
        return interpolate(other, { count: `${plural}`, ...(opts || {}) });
      }
      return "";
    }
    // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
    if (translations[key]) {
      return translations[key].value;
    }
    return "";
  };
}

/**
 * Interpolate a string with an object
 * the template contains double brackets with the key {{ key }} or {{key}}
 */
function interpolate(tpl: string, obj: Record<string, string>) {
  return tpl.replace(/{{\s*([\w.]+)\s*}}/g, (match, key) => {
    return obj[key] || "";
  });
}

/* eslint-disable @typescript-eslint/ban-ts-comment */
import { type ReactNode, type Ref, cloneElement, isValidElement } from "react";

import { mergeRefs } from "./Utils/mergeRef";

export type SproutGeneratorFunction<T extends ReactNode, P, R = unknown> = (
  props: P,
  ref?: R,
) => T;

export type ChildOrGenerator<T extends ReactNode, P, R = unknown> =
  | T
  | SproutGeneratorFunction<T, P, R>;

/**
 * This function iterate over props to add keys into a copy of nextProps.
 * This is a limitation of cloneElement it manage overrides so we have to manage merge.
 */
function mergeProps<T extends ReactNode>(
  child: T,
  nextProps: Record<string, unknown> & { ref?: Ref<HTMLElement> },
  nextRef?: Ref<HTMLElement> | undefined,
) {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  let props: Record<string, any> & { ref?: Ref<HTMLElement> } = {};
  let refProps: Ref<HTMLElement> | undefined;
  const nextPropsRef: Ref<HTMLElement> | undefined = nextProps.ref;
  let ref: Ref<HTMLElement> | undefined;
  if (isValidElement(child)) {
    props = child.props as Record<string, unknown> & { ref?: Ref<HTMLElement> };
    // @ts-ignore
    ref = child.ref;
    if (props.ref) {
      refProps = props.ref;
    }
  }
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const returnProps = Object.keys(nextProps).reduce<Record<string, any>>(
    (acc, key) => {
      if (key === "ref") {
        return acc;
      }
      // @ts-ignore
      if (props[key]) {
        // event handler
        // @ts-ignore
        if (typeof props[key] === "function") {
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          acc[key] = (...args: Array<any>) => {
            // @ts-ignore
            if (props[key] && typeof props[key] === "function") {
              // @ts-ignore
              props[key](...args);
            }
            if (nextProps[key] && typeof nextProps[key] === "function") {
              nextProps[key](...args);
            }
          };
        } else {
          // override
          acc[key] = nextProps[key];
        }
      }
      return acc;
    },
    { ...nextProps },
  );

  const toMergeRef = [nextRef, refProps, nextPropsRef];
  if (ref && ref !== refProps && ref !== nextPropsRef) {
    toMergeRef.push(ref);
  }

  returnProps.ref = mergeRefs(toMergeRef.filter(Boolean));
  return returnProps;
}

/**
 * Utility function for `renderOrClone`.
 * This function is a trick to check if given element is a function but without loosing expected signature (generator function).
 * Is is barely like casting function when invoking it but in more elegant way.
 * @param element element to check
 * @returns true if element is a function. Because function's signature is restricted (see type `GeneratorFunction`) we can
 * infer it is a generator function
 */
function isGeneratorFunction<T extends ReactNode, P, R = unknown>(
  element: ChildOrGenerator<T, P, R>,
): element is SproutGeneratorFunction<T, P, R> {
  return typeof element === "function";
}

export function renderOrClone<T extends ReactNode, P, R = Ref<unknown>>(
  children: ChildOrGenerator<T, P, R>,
  props: P,
  ref?: R,
) {
  if (isGeneratorFunction(children)) {
    return children(props, ref);
  }

  if (isValidElement(children) && props) {
    // @ts-ignore
    const newProps = mergeProps(children, props, ref);
    // eslint-disable-next-line @eslint-react/no-clone-element
    return cloneElement(children, newProps);
  }

  // If here it means "children" is not a function nor a clonable element - so return it
  return children;
}
