import type { PropsWithChildren } from "react";

import {
  DocsContainer as BaseContainer,
  type DocsContainerProps,
} from "@storybook/addon-docs/blocks";

import { type Mode, getMode } from "./theme";

export function DocsContainer({
  children,
  context,
}: PropsWithChildren<DocsContainerProps>) {
  const backgroundsValue = (context as any)?.store?.userGlobals?.globals
    ?.mode as Mode;

  const theme = getMode(backgroundsValue);

  return (
    <BaseContainer theme={theme} context={context}>
      {children}
    </BaseContainer>
  );
}

import { addons } from "storybook/manager-api";

import favicon from "./favicon.png";
import {
  LOCAL_STORAGE_KEY,
  MODE,
  type Mode,
  darkTheme,
  getMode,
  lightTheme,
} from "./theme";

addons.setConfig({
  theme: getMode(),
});

addons.register("sprout-css/mode-switcher", () => {
  const channel = addons.getChannel();

  channel.on("globalsUpdated", ({ globals }) => {
    const mode = globals.mode as Mode;

    if (mode === MODE.DARK || mode === MODE.LIGHT) {
      window.localStorage.setItem(LOCAL_STORAGE_KEY, mode);

      addons.setConfig({
        theme: mode === MODE.DARK ? darkTheme : lightTheme,
      });
    }
  });
});

const link = document.querySelector<HTMLLinkElement>(`link[rel="icon"]`);
if (link) {
  link.setAttribute("type", "image/png");
  link.setAttribute("href", favicon);
  document.head.appendChild(link);
}

import { create } from "storybook/theming";

import darkModeLogo from "./sprout-css-dark.png";
import logo from "./sprout-css.png";

export const LOCAL_STORAGE_KEY = "sprout-storybook-theme";

export const lightTheme = create({
  base: "light",
  brandTitle: "Sprout CSS & React",
  brandImage: logo,
  brandTarget: "_self",
  appPreviewBg: "#FFFFFF",
});

export const darkTheme = create({
  base: "dark",
  brandTitle: "Sprout CSS & React",
  brandImage: darkModeLogo,
  brandTarget: "_self",
  appPreviewBg: "#000000",
});

export const MODE = {
  LIGHT: "light",
  DARK: "dark",
} as const;

export type Mode = (typeof MODE)[keyof typeof MODE];

const prefersDarkMode =
  typeof window !== "undefined" &&
  window.matchMedia &&
  window.matchMedia("(prefers-color-scheme: dark)").matches;

export const getSystemMode = (): Mode =>
  prefersDarkMode ? MODE.DARK : MODE.LIGHT;

function getStoredMode(): Mode | undefined {
  if (typeof window === "undefined") {
    return undefined;
  }
  const stored = window.localStorage.getItem(LOCAL_STORAGE_KEY);
  if (stored === MODE.DARK || stored === MODE.LIGHT) {
    return stored;
  }

  const systemMode = getSystemMode();

  return systemMode;
}

export function getMode(mode?: Mode) {
  const storedMode = getStoredMode();
  const resolved = mode ?? storedMode ?? getSystemMode();

  return resolved === MODE.DARK ? darkTheme : lightTheme;
}

export function getInitialMode() {
  const storedMode = getStoredMode();
  return {
    mode: storedMode ?? getSystemMode(),
  };
}

